@mixin translate3d($tx, $ty, $tz) {
  -webkit-transform: translate3d($tx, $ty, $tz);
     -moz-transform: translate3d($tx, $ty, $tz);
      -ms-transform: translate($tx, $ty);
       -o-transform: translate3d($tx, $ty, $tz);
          transform: translate3d($tx, $ty, $tz)
}

