  overflow-x: hidden;
  overflow-y: auto;
  position: absolute;
  transition: transform 500ms ease 0s;
  width: $off-canvas-width;
  z-index: 1001;

  @if $position == left {
    @include translate3d(-100%,0,0);
    left: 0;
    top: 0;
  }
  @if $position == right {
    @include translate3d(100%,0,0);
    right: 0;
    top: 0;
  }
  @if $position == top {
    @include translate3d(0,-100%,0);
    top: 0;
    width: 100%;
    height: $off-canvas-height;
  }
  @if $position == bottom {
    @include translate3d(0,100%,0);
    bottom: 0;
    width: 100%;
    height: $off-canvas-height;
  }
