common/focus-tooltip.scss:
@mixin addFocusTooltip ($attr: title) {
    &::before {
        border-color: transparent transparent $focusTooltip-backgroundColor transparent;
        border-style: solid;
        border-width: remCalc(10px);
        content: " ";
        position: absolute;
        right: 0;
        top: 50%;
    }

    &::after {
        background-color: $focusTooltip-backgroundColor;
        border-radius: remCalc(8px);
        color: $focusTooltip-textColor;
        content: attr($attr);
        cursor: default;
        font-size: 1rem;
        padding: remCalc(4px) remCalc(6px);
        position: absolute;
        right: 0;
        top: 100%;
        white-space: nowrap;
    }

    &::before,
    &::after {
        display: none;
    }

    &:focus {
        &::before,
        &::after {
            display: block;
        }
    }
}

components/citadel/nav/nav.scss:
// =============================================================================
// NAV (CSS)
// =============================================================================


// Citadel inline messages
// -----------------------------------------------------------------------------
//
// 1. Remove the margin-left we add to default lists.
//
// -----------------------------------------------------------------------------

.navList,
.navBar {
    margin-left: 0; // 1
}

.navList {
    margin-bottom: 20px;
}

.navList-action,
.navBar-action {
    text-decoration: none;
}

.navList-action {
    font-size: $fontSize-root;
    color: #404040;
}

.navList-action.is-active {
    font-weight: $navList-action-fontWeight--active;
}


// NavList action checkbox
// -----------------------------------------------------------------------------
//
// Purpose: Display a checkbox next to an actionable link, without actually using
// a checkbox element.
//
// 1. Position checkbox so it is vertically centered
//
// -----------------------------------------------------------------------------

.navList-action--checkbox {
    @include checkRadioBase();
    @include checkBox-base();

    &::before,
    &::after {
        top: 50%; // 1
    }

    &::before {
        margin-top: -$checkRadio-outerSize / 2;
    }

    &::after {
        margin-top: -$checkRadio-outerSize / 2 + remCalc(1);
    }

    &:focus {
        @include checkRadioBase--focus();
    }

    &.is-disabled {
        @include checkRadioBase--disabled();
    }

    &.is-checked,
    &.is-selected {
        @include checkRadioBase--checked();
    }
}


// NavList action close
// -----------------------------------------------------------------------------
//
.navList-action-close {
    background: $navList-action-close-background;
    border-radius: $navList-action-close-borderRadius;
    display: inline-block;
    float: right;
    line-height: 0;
    opacity: 0;
    padding: $navList-action-close-padding;
    transition: $navList-action-close-transition;
    visibility: hidden;

    .icon {
        @include square($navList-action-close-iconSize);
        fill: stencilColor("color-textLink");
    }

    .navList-action.is-selected:hover & {
        opacity: 1;
        visibility: visible;
    }
}


// Page Subnavigation
// -----------------------------------------------------------------------------

.navBar--sub {
    margin-bottom: spacing("single") * 3;
    text-align: center;

    .navBar-section {
        float: none;
    }

    .navBar-item {
        float: none;
        margin: 0 spacing("half");

        @include breakpoint("medium") {
            display: inline-block;
        }

        &.is-active {
            > .navBar-action {
                color: color("greys", "dark");
            }
        }

        &.navBar-item--separate {
            @include breakpoint("medium") {
                border-left: 1px solid $header-font-color;
                margin: 0;
                padding-left: spacing("half");
            }
        }
    }

    .navBar-action,
    .navBar-action-color--active {
        font-size: fontSize("base");
    }

    .navBar-action {
        color: $header-font-color;
        padding: 0;

        &:hover {
            color: $color-textBase-hover;
        }
    }
}

