}

.account-product-title {
    +.is-read {
        margin: 0;
        padding: 15px 0 0 0;
    }
}


.account-product-refundQty {
    color: color("greys", "light");
    margin-bottom: 0;
}

.account-product-details {
    @include grid-row($behavior: "nest");
}

.account-product-detail {
    @include grid-column(12);

    +.account-product-detail {
        margin-top: spacing("base");
    }

    +.account-product-detail--full {

        @include breakpoint("small") {
            margin-top: spacing("single");
        }
    }

    @media (min-width: 551px) {
        width: 50%;

        +.account-product-detail {
            margin-top: 0;
        }
    }

    @media (min-width: 768px) {
        width: 25%;
    }

    @media (min-width: 1025px) {
        width: auto;

        +.account-product-detail {
            margin-left: 35px;
        }
    }

    @media (min-width: 1600px) {
        width: auto;

        +.account-product-detail {
            margin-left: 75px;
        }
    }
}

.account-product-detail--large {

    @include breakpoint("large") {
        width: grid-calc(6, $total-columns);
    }
}

.account-product-detail--full {
    width: 100%;
}

.account-product-detail-heading {
    color: $header-font-color;
    margin-top: 0;
    margin-bottom: 5px;

    +span {
        display: block;
    }
}


// Vertically aligned product view
// -----------------------------------------------------------------------------

.account-product--alignMiddle {
    display: table;
    width: 100%;

    .account-product-checkItem {
        display: table-cell;
        position: relative;
        vertical-align: middle;
        width: 2rem;

        @include breakpoint("large") {
            left: -2.5rem;
        }

        // scss-lint:disable SelectorDepth, NestingDepth
        .form-label {
            height: spacing("single");
            margin: 0;
            padding: 0;
            width: spacing("single");
        }
    }

    .account-product-figure,
    .account-product-body {
        display: table-cell;
    }

    .account-product-figure {
        float: none;
        margin-right: 0;

        @include breakpoint("large") {
            left: -2rem;
        }
    }

    .account-product-body {
        padding-left: 2rem;
        vertical-align: middle;

        @include breakpoint("large") {
            padding-left: 0;
        }
    }
}


// Account product totals
// -----------------------------------------------------------------------------

.account-orderTotal {
    float: right;
    margin: 0;
    width: grid-calc(8, $total-columns);
}

.account-orderTotal-key,
.account-orderTotal-value {
    margin: 0;
    padding: 15px 0;
    color: $color-textBase;
}

.account-orderTotal-key {
    color: $color-textBase;
    float: left;

    &:last-of-type {
        position: relative;
        top: spacing("fifth");
    }
}

.account-orderTotal-value {
    border-bottom: 1px solid $color-custom-grey;
    text-align: right;

    &:last-child {
        border-bottom: 0;
    }
}


// Account status
// -----------------------------------------------------------------------------

.account-orderStatus {
    @media (min-width: 551px) {
        float: right;
        position: inherit;
        text-align: center;
        bottom: 0;
        left: 0;
    }
}

.account-orderStatus-label {
    background-color: $global-color;
    color: $color-white;
    display: inline-block;
    vertical-align: top;
    margin-bottom: 20px;
    padding: 0 5px;
    margin-top: 0;

    @media (min-width: 551px) {
        margin-top: 0;
        margin-bottom: 0;
        min-width: 122px;

        +.account-orderStatus-action {
            margin-top: 10px;
        }
    }
}

.account-orderStatus-action {
    display: inline-block;
    margin: 10px 0 0 15px;
    text-align: right;

    @media (min-width: 551px) {
        display: block;
        margin-top: 0;
        margin-left: 0;
    }
}

.page-type-account_returns {
    .account-product-title {
        +.account-product-description {
            top: unset;
        }
    }

    .account-product-description {
        a {
            font-style: italic;
            text-decoration: underline;
            text-underline-position: under;
        }

        +.definitionList {
            margin-top: 0;
        }
    }

    .definitionList {
        margin-bottom: 20px;
    }
}

// Account pagination & panel
// -----------------------------------------------------------------------------

.account {
    .pagination {
        display: block;
        text-align: left;
        margin-top: 18px;
        margin-bottom: -12px;
    }
}

// Sidebar blocks
// -----------------------------------------------------------------------------

.account-sidebar-block {
    font-size: fontSize("smaller");

    +.account-sidebar-block {
        margin-top: spacing("double");
    }

    .account-heading {
        margin-bottom: spacing("single");
    }

    .button {
        text-transform: uppercase;
        min-width: 200px;
        margin-bottom: 10px;
    }
}

.account-order-address {
    margin-bottom: 0;
    @include u-listBullets("none");
}

.account-reorder-form {
    display: inline-block;
}

.order-payments-description {
    color: stencilColor("color-primary");
    font-weight: fontWeight("normal");
    margin-top: spacing("single");
}


// Product downloads
// -----------------------------------------------------------------------------

.account-downloads-summary {
    border-bottom: container("border");
    font-size: fontSize("smaller");
    padding-bottom: spacing("single");
}

.account-downloadsList {
    @include u-listBullets("none");
}

.account-downloadsItem {
    margin-bottom: spacing("single");

    > :first-child {
        margin-top: 0;
    }

    > :last-child {
        margin-bottom: 0;
    }
}

.account-downloadsItem-title {
    color: $header-font-color;
    font-size: fontSize("smaller");
    margin-bottom: spacing("eighth");
    text-transform: inherit;
}

.account-downloadsItem-description {
    margin-bottom: 0;
}

.account-downloadsItem-availability {
    color: $header-font-color;
}

.page {
    &.account {
        .button {
            @media (min-width: 381px) {
                min-width: 205px;
            }
        }
    }
}

.page.page-giftcertificates,
.page.page-account {
    margin: 0;

    .page-header {
        margin-bottom: 28px;
    }

    .page-content {
        padding-left: 0;
        padding-right: 0;
    }
}

.page.page-giftcertificates {
    .page-content {
        .account {
            >h3 {
                font-weight: 600;
            }

            .intro {
                font-weight: 500;
            }

            .form-prefixPostfix {
                max-width: 865px;

                #giftcertificatecode {
                    width: calc(100% - 225px);
                }
            }
        }
    }
}

.form-label {
    color: $header-font-color;

    small {
        float: none;
    }
}

.form-select {
    height: 50px;
    min-height: 50px;
    border-color: stencilColor("input-border-color");
}

.page-account.account-created {
    margin-top: 50px;
    margin-bottom: 50px;

    .page-header {
        @media (max-width: 1024px) {
            margin-bottom: 25px;
        }
    }

    .button {
        @media (max-width: 767px) {
            width: 100%;
        }
    }
}

