layouts/header/header.scss:
// =============================================================================
// HEADER (CSS)
//
// 1. Setting font size of header-logo to 0, since wrapper element may vary, and
//    inline-block whitespace messes with header height. Font-size is reset to
//    appropriate size on header-logo-text.
// 2. Needs left/right margin so it doesn't cover left/right nav bar toggles
// 3. Offset the letter-spacing on the last character by adding a right negative
//    margin. This centers the text logo properly.
// 4. When the word "cart" is added to the mobile header's cart link, add more
//    margin to compensate.
// 5. When logo size is set to "original", we don't have advance knowledge of the
//    image size, so we can't use absolute positioning + padding to reserve space
//    for lazy loading.
// 6. When logo size is set to "original" and switch to mobile version, it keeps
//    content in center regardless its size.
//
// -----------------------------------------------------------------------------

.skip-to-main-link {
    background: $header-bg;
    color: $color-textBase;
    font-weight: 700;
    left: 50%;
    padding: 4px;
    position: absolute;
    transform: translate(-50%, calc(-100% - #{$header-height}));
    transition: transform 0.3s;
    z-index: zIndex("highest");

    &:focus {
        transform: translate(-50%, 0%);
    }
}

.header {
    position: relative;
    background-color: $header-bg;
    height: auto;
    width: 100%;
    z-index: zIndex("higher");

    .halo-bottomHeader {
        background-color: $color-white;
    }

    .navUser-section--link {
        .navUser-action {
            font-size: $fontSize-root;
            font-weight: 800;
        }
    }

    &.is-sticky {
        background-color: $header-bg;
        box-shadow: none;
        position: fixed;
        top: 0;
        animation: halo-fadeInDown .6s cubic-bezier(0, 0, .3, 1) forwards;
    }
}

.header-logo {
    font-size: 0;   // 1
    height: $header-height;
    margin: 0; // 2
    text-align: center;

    @include breakpoint("small") { // 4
        margin-left: remCalc($header-toggle-width * 1.5);
        margin-right: remCalc($header-toggle-width * 1.5);
    }

    @include smin-m {
        height: auto;
        margin: (spacing("double") + spacing("base")) auto (spacing("double") + spacing("single"));
        padding: 0;

        .header.fixed & {
            background-color: color("greys", "lightest");
            margin: 0;
            padding: spacing("quarter") 0;
            position: absolute;
            top: 0;
            width: 100%;
        }
    }

    &__link {
        @include clearfix;
        align-items: center;
        color: $storeName-color;
        display: flex;
        height: inherit;
        justify-content: center; // 6
        margin: 0 auto;
        position: relative;
        text-decoration: none;
        width: 70%;
        z-index: zIndex("low");

        @include smin-m {
            background: none;
            border-bottom: 0;
            display: inline-flex;
            padding: 0;
            width: auto;
        }

        &:hover {
            color: color("link", "hover");
        }

        &:active {
            color: color("link", "active");
        }
    }
}

.header-logo--left {
    text-align: left;

    @include smin-m {
        margin-left: remCalc(40px);
    }

    .header-logo-image {
        right: unset;
    }
}

.header-logo--right {
    text-align: right;

    @include smin-m {
        margin-right: remCalc(40px);
    }

    .header-logo-image {
        left: unset;
    }
}

.header-logo-text {
    display: block;
    font-family: $fontFamily-hero;
    font-size: $fontSize-logo-small;
    font-weight: stencilFontWeight("headings-font");
    letter-spacing: remCalc(2px);
    margin-left: auto;
    margin-right: auto;
    overflow: hidden;
    text-overflow: ellipsis;
    text-transform: uppercase;
    white-space: nowrap;

    @include breakpoint("small") {
        font-size: $fontSize-logo-medium;
    }

    @include smin-m {
        display: inline;
        font-size: $fontSize-logo-large;
        max-width: none;
        padding: 0 10px;
        white-space: normal;

        .header.fixed & {
            font-size: fontSize("larger");
        }
    }
}

.header-logo-image-container {
    position: relative;
    width: 100%;

    @include smin-m {
        min-height: get-height(stencilString("logo_size"));
    }
}

.header-logo-image-container::after {
    content: "";
    display: block;
}

.header-logo-image {
    display: block;
    margin: 0 auto;
    max-height: 24px;
    border-radius: 0;

    @include smin-s {
        max-height: 40px;
    }

    @include smin-m {
        max-height: none;
    }
}

.header-logo-image-unknown-size { // 5
    max-height: remCalc($header-height) - $header-logo-marginVertical * 2;

    @include smin-m {
        max-height: none;
    }
}

.navUser-section--top {
    svg {
        margin: -1px 6px 0 0;
    }
}

.navUser-section--action {
    svg {
        @include square(32px);

        @include smin-xxxl {
            @include square(22px);
        }
    }
}

.contact-phone {
    color: $header-color;
    margin-left: 24px;

    p {
        margin-bottom: 4px;
    }

    a {
        color: $header-color;
        font-weight: $fontWeight-bold;
    }
}

.halo-topHeader {
    display: none;
    position: relative;
    z-index: -1;
    opacity: 0;
    visibility: hidden;
    pointer-events: none;

    .item--quicksearch + .contact-phone {
        min-width: 150px;
    }

    @include smin-m {
        display: block;
        z-index: unset;
        opacity: 1;
        visibility: visible;
        pointer-events: auto;
        min-height: 96px;

        .container {
            display: flex;
            flex-wrap: nowrap;
            align-items: center;
            justify-content: space-between;
            position: unset;

            .topHeader-item {
                display: flex;
                flex-wrap: nowrap;
                align-items: center;
                justify-content: left;
                padding: 0;

                &__item {
                    display: block;
                    width: 100%;

                    &:not(.item--sticky-logo) {
                        padding-left: 35px;
                    }

                    .header-logo {
                        margin: 0;
                    }
                }

                &.right {
                    .navUser {
                        .navUser-section {
                            display: none;
                            vertical-align: middle;
                            display: inline-block;
                            margin-left: 0;
                        }
                    }
                }

                &.center {
                    justify-content: center;
                }

                .halo-cart {
                    display: none;
                }
            }
        }

        &.header_logo--center {
            .container {
                .topHeader-item {
                    &.center {
                        width: 280px;
                        text-align: center;
                    }

                    &.left,
                    &.right {
                        width: calc((100% - 280px)/ 2);
                    }

                    &.right {
                        justify-content: right;
                        text-align: right;

                        .navUser {
                            width: auto;
                        }
                    }
                }
            }
        }
    }
}

.halo-middleHeader {
    z-index: 11;
    padding: 2px 0 3px;
    margin: 0;
    position: relative;

    .container {
        width: 100%;
        max-width: 100%;
        display: flex;
        flex-wrap: nowrap;
        align-items: center;
        justify-content: space-between;

        .middleHeader-item {
            display: flex;
            flex-wrap: nowrap;
            align-items: center;
            justify-content: space-between;
            padding: 0;

            &.left,
            &.right {
                min-width: 98px;

                @include smin-s {
                    min-width: 141px;
                }
            }

            &__item {
                display: inline-block;
                width: 100%;
                margin-right: 10px;

                &:last-child {
                    margin-right: 5px;
                }

                &.item--hamburger {
                    margin: 0;
                }

                &:not(.item--logo) {
                    width: auto;
                }

                @include smin-s {
                    margin-right: 20px;
                }
            }

            &.center {
                flex: 1;
            }
        }
    }

    .navUser-action {
        padding: 0;
        transition: unset;

        .navUser-icon {
            display: inline-block;
            vertical-align: top;
            position: relative;

            svg {
                @include square(24);
                margin: 0;
                transition: unset;

                @include smin-s {
                    @include square(32);
                }
            }
        }
    }

    @include smin-s {
        padding: 12px 0 13px;
    }

    @include smin-m {
        display: none;
    }
}

.halo-bottomHeader {
    display: none;
    position: relative;
    z-index: -1;
    opacity: 0;
    visibility: hidden;
    pointer-events: none;

    .navUser-item,
    .navUser-action {
        color: $navPage-Color;
    }

    svg {
        fill: $navPage-Color;
    }

    .cur-flag {
        img {
            width: 24px;
            height: 24px;
            border-radius: 50%;
            padding: 2px;
        }
    }

    @include smin-m {
        display: block;
        z-index: unset;
        opacity: 1;
        visibility: visible;
        pointer-events: auto;

        .navUser-action {
            &:hover {
                color: $navPage-Color;

                svg {
                    fill: $navPage-Color;
                }
            }
        }

        .container {
            display: flex;
            flex-wrap: nowrap;
            align-items: center;
            justify-content: space-between;
            position: unset;
        }

        .has-megamenu {
            .container {
                align-items: flex-start;
            }
        }
    }
}

.bottomHeader-item {
    &.right {
        min-width: 300px;

        .navUser-section {
            display: flex;
            justify-content: flex-end;
        }
    }

    .navUser-item + .navUser-item {
        margin-left: 24px;
        padding-left: 24px;
        border-left: 1px solid #a9a9a9;
    }
}

//
// Mobile Menu Toggle
//
// 1. Animate lines from 'x' to hamburger
// -----------------------------------------------------------------------------

.mobileMenu-toggle {
    width: 24px;
    height: 24px;
    padding: 3px;
    font-size: 0;
    overflow: hidden;
    position: relative;
    display: block;
    z-index: 1;
    outline: none;
    box-shadow: none;

    .mobileMenu-toggleIcon,
    .mobileMenu-toggleIcon::before,
    .mobileMenu-toggleIcon::after {
        content: "";
        background: $color-white;
        display: block;
        height: 2px;
        width: 18px;
        position: absolute;
        transform: rotate(0deg);
        transition: transform 100ms ease-in, top 100ms ease-in 150ms, bottom 100ms ease-in 150ms;

        @include smin-xs {
            width: 24px;
        }
    }

    .mobileMenu-toggleIcon {
        top: 50%;
        width: 18px;
        transform: translateY(-50%);
        transition: background-color 10ms ease-in 100ms;

        @include smin-s {
            width: 24px;
        }
    }

    .mobileMenu-toggleIcon::before {
        top: -8px;

        @include smin-s {
            top: -10px;
        }
    }

    .mobileMenu-toggleIcon::after {
        bottom: -8px;

        @include smin-s {
            bottom: -10px;
        }
    }

    &.is-open {
        .mobileMenu-toggleIcon {
            background-color: transparent;
            transition-delay: 100ms;

            &::before,
            &::after {
                bottom: auto;
                top: auto;
                transition: transform 100ms ease-in 150ms, top 100ms ease-in, bottom 100ms ease-in;
            }

            &::before {
                top: 0;
                transform: rotate(45deg);
            }

            &::after {
                bottom: 0;
                transform: rotate(-45deg);
            }
        }
    }

    @include smin-s {
        width: 32px;
        height: 32px;
        padding: 4px;
    }
}

layouts/body/body.scss:
// =============================================================================
// BODY (CSS)
//
// 1. Header is fixed on small screens, use the content body to create the whitespace
//    between it and the header on all situations. If you change the spacing here,
//    please update .heroCarousel too.
//
// =============================================================================

html,
body {
    scroll-behavior: smooth;
    padding: 0;
}

.body {
    margin-bottom: 50px;
    margin-top: 0;
    line-height: 1.375;
    letter-spacing: $letterSpacing-base;

    .page-type-default &,
    .page-type-product &,
    .page-type-blog &,
    .page-type-page &,
    .page-type-page_contact_form & {
        margin-bottom: 0;
    }
}

.page {
    @include grid-row($behavior: nest);
    margin: 0;
}

.page-content,
.page-content--full {
    @include grid-column(12);
    padding: 0
}

.page-region {
    @include grid-column(12);
    float: right;
    width: 100%;

    @include breakpoint("medium") {
        width: 75%;
    }
}

.page-content--centered {

    @include breakpoint("medium") {
        @include grid-column($columns: 8,
            $center: true);
    }
}

.page-content--textCenter {
    text-align: center;
}

.page-sidebar {
    padding: 0;
    width: 100%;
    float: left;
    position: relative;
    background-color: $body-bg;

    &.u-hidden {
        ~.page-content {
            padding: 0;
            width: 100%;
        }
    }

    .halo-sidebar-header {
        .title {
            padding: 8px;
            border-bottom: none;
        }
    }

    nav {
        display: block;
        width: 100%;
        height: calc(100% - 60px);
        overflow-x: hidden;

        >div:last-child {
            border-bottom: 0;
        }
    }

    .number {
        @include square(24);
        margin-left: 10px;
    }

    @include smin-m {
        width: 270px;

        .halo-sidebar-header {
            display: none;
        }

        +.page-content {
            padding-left: 10px;
            width: calc(100% - 270px);
        }
    }

    @include smin-xl {
        +.page-content {
            padding-left: 30px;
        }
    }

    @include smin-xxl {
        width: 370px;

        +.page-content {
            width: calc(100% - 370px);
        }
    }

    @include smax-m {
        position: fixed;
        top: 0;
        right: -101%;
        bottom: 0;
        width: 100%;
        height: 100%;
        z-index: 102;
        max-width: 410px;
        transition: $anchor-transition;

        &.is-open {
            .openSidebar & {
                right: 0;
            }
        }

        .haloCustomScrollbar {
            padding: 30px 20px 20px;
        }
    }
}

.page-sidebar-mobile-wrapper {
    display: inline-block;
    position: relative;
    margin: 0 0 20px 0;

    .page-sidebar-mobile {
        position: relative;
        display: inline-block;
        z-index: 2;
        cursor: pointer;
        pointer-events: auto;
        border: 1px solid #a9a9a9;
        padding: 8px 16px;

        .text {
            font-size: $fontSize-root;
            font-weight: $fontWeight-semibold;

            .page-blog & {
                display: inline-block;
                margin-right: 10px;
            }
        }

        .icon {
            @include square(16);
            margin-left: 15px;
        }
    }

    @include smin-m {
        display: none;
        margin-bottom: 0;
    }
}

layouts/footer/footer.scss:
// =============================================================================
// FOOTER (CSS)
// =============================================================================


// Layout and columns
// -----------------------------------------------------------------------------
//
// 1. To enable the grid's display: inline-block; to work properly, we need to
//    remove the white-space that it creates between columns with font-size: 0;
// 2. Re-set the font-size that was reduced to 0 in point 1 for children.
// 3. Fix for text flickers on windows devices
//
// -----------------------------------------------------------------------------

.footer {
    background-color: $footer-background;
    padding: 30px 0 20px;
    position: relative;
    transform: translateZ(0); // 3

    @include smin-m {
        padding: 80px 0 20px;
    }
}

.footer-title-sr-only {
    height: 1px;
    left: -10000px;
    overflow: hidden;
    position: absolute;
    top: auto;
    width: 1px;
}

.footer-info {
    font-size: 0; // 1
}

.footer-info-col {
    width: 100%;
    padding: 0;
    color: $header-font-color;
    display: inline-block;
    float: none;
    font-size: fontSize("base"); // 2
    margin-bottom: 0;
    overflow-wrap: break-word;
    vertical-align: top;
    // TODO: specifically use vendor only for IE11 since overflow-wrap is not supported here
    -ms-word-break: break-all;

    @include smin-xs {
        width: width("4/12");
        margin-bottom: 30px;
    }

    @include smin-m {
        margin-bottom: 80px;
        padding: 0 20px 0 0;
        width: width("6/12");
    }

    @include smax-m {
        &[data-section-type="footer-social"] {
            padding: 30px 0;
            margin: 0;
            width: 100%;
        }
    }

    @include smax-xs {
        &[data-section-type="footer-social"] {
            border-top: 1px solid $color-textSecondary;
        }
    }
}

.footer-info-col--small {
    @include breakpoint("medium") {
        width: width("3/12");
    }
}


// Content
// -----------------------------------------------------------------------------

.footer-info-heading {
    color: $footer-heading-fontColor;
    text-transform: inherit;
    margin-bottom: 8px;
    padding: 3px 0;
}

.footer-info-heading--toggle {
    @include smax-xs {
        border-top: 1px solid $color-textSecondary;
        margin-bottom: 0;
        padding: 21px 0;
        position: relative;

        &:hover {
            cursor: pointer;
        }

        &:before {
            top: 50%;
            right: 5px;
            z-index: 2;
            content: "";
            width: 2px;
            height: 12px;
            position: absolute;
            transition: all .4s ease;
            transform: translateY(-50%);
            background-color: stencilColor("footer-heading-fontColor");

            .open-dropdown & {
                transform: translateY(-50%) rotate(90deg);
            }
        }

        &:after {
            top: 50%;
            right: 0;
            z-index: 2;
            content: "";
            width: 12px;
            height: 2px;
            position: absolute;
            transform: translateY(-50%);
            background-color: stencilColor("footer-heading-fontColor");
        }
    }
}

.footer-intro {
    color: $footer-heading-fontColor;
    margin-bottom: 24px;
}

.footer-info-list {
    list-style: none;
    margin-left: 0;

    li {
        margin-bottom: 8px;
        padding: 3px 0;

        &:last-child {
            margin-bottom: 0;
        }
    }

    a {
        color: $footer-heading-fontColor;
        text-decoration: none;

        @include smin-m {
            &:hover {
                color: $footer-text-colorHover;
            }
        }
    }

    @include smax-xs {
        display: none
    }
}

address,
.phone {
    color: $footer-heading-fontColor;
}

.footer-copyright {
    padding: 16px 0;
    text-align: center;
    border-top: 1px solid #a9a9a9;
    border-bottom: 1px solid $color-white;

    @include smin-xs {
        padding: 24px 0;
        text-align: left;
    }

    svg {
        @include square(32);
    }

    >.powered-by {
        color: $header-font-color;

        @include smax-xs {
            max-width: 282px;
            margin: 0 auto 16px;
        }
    }

    a {
        color: $header-font-color;
        text-decoration: none;

        @include smin-m {
            &:hover {
                color: $color-textLink-hover;
            }
        }
    }

    @include smax-m {
        .socialLinks-item {
            margin: 10px 10px 10px 0;
        }
    }
}

.footer-payment-icons {
    .footer-payment-icon {
        height: remCalc(26);
        margin: 4px;
        vertical-align: middle;
        width: remCalc(40);
        border: 1px solid #c7c7c7;
        background-color: #ffffff;

        @include smin-xs {
            margin: 6px;
        }
    }

    svg {
        fill: stencilColor("icon-color");
    }
}

.footer-app {
    font-size: 0;

    @include smax-xs {
        img {
            max-width: 87px;
        }
    }
}

layouts/blog/blog.scss:
// =============================================================================
// BLOG
// =============================================================================


// Blog post
// -----------------------------------------------------------------------------

.blog {
    margin-bottom: spacing("single");

    @include breakpoint("medium") {
        margin: auto;
        width: 100%;
    }

    .tags {
        margin: 0;

        .tag {
            background: #eeeeee;
            a {
                color: #2a2a2a;
            }
        }
    }

    .socialLinks {
        text-align: center;
    }

    .addthis_toolbox {
        margin-top: spacing("double");
    }
}

.blog-title {
    font-size: $h2-font-size * 0.59090909;
    margin: 40px 0 20px;

    @include smin-xs {
        font-size: $h2-font-size * 0.6818181818;
    }

    @include smin-m {
        font-size: $h2-font-size;
    }
}

.blog-date,
.blog-author {
    color: $header-font-color;
}

.blog-tag,
.blog-author {
    margin-bottom: 18px;
}

.blog-post {
    font-size: $fontSize-root - 4;
    text-align: left;

    @include smin-xs {
        font-size: $fontSize-root - 2;
    }

    @include smin-m {
        font-size: $fontSize-root;
    }
}

.blog-thumbnail {
    position: relative;

    img {
        width: 100%;
    }
}

.blog-post-info {
    margin-bottom: 60px;
}

.blog-post-figure {
    display: block;
    margin: auto auto 60px;
    text-align: center;

    img {
        min-height: 200px;
        max-height: 200px;
        object-fit: cover;

        @include smin-xs {
            min-height: 320px;
            max-height: 320px;
        }

        @include smin-m {
            min-height: 550px;
            max-height: 550px;
        }
    }
}

.blog-post-body {
    display: block;
    margin: 0 auto;
    text-align: center;
    width: 100%;
}

.blog-post-detail {
    line-height: 1.75;

    h6 {
        line-height: 1.6
    }

    .customer {
        margin: 40px 0;

        .content {
            max-width: 864px;
            margin: 59px auto;
        }

        .title {
            font-size: 18px;
            line-height: 1.267;

            @include smin-xs {
                font-size: 26px;
            }

            @include smin-m {
                font-size: 30px;
            }
        }
    }

    .name {
        line-height: 1.375;
    }

    .blockQuote {
        padding: 12px;

        @include smin-xs {
            padding: 18px;
        }

        @include smin-m {
            padding: 24px;
        }
    }
}

.blog-footer {
    margin-top: 60px;
    padding: 2px 10px 10px;

    .text {
        margin-left: 6px;
    }

    .action {
        min-width: 200px;
    }

    @include smin-xs {
        padding: 8px 30px;
    }

    @include smin-m {
        padding: 12px 40px;
    }
}

.shareLinkSocial {
    margin: 0;
    font-size: 0;
    letter-spacing: 0;

    .socialLinks {
        font-size: 0;

        .icon {
            position: unset;
        }
    }

    &__popup {
        display: none;
        position: absolute;
        top: 100% !important;
        left: auto !important;
        right: 0;
        z-index: 101;
        padding: 10px 20px;
        width: 250px;
        min-width: 250px;
        background-color: $color-white;
        box-shadow: 0px 1px 20px 0px rgba(0, 0, 0, 0.1);

        .socialLinks-item--print {
            display: none;
        }

        .icon,
        .icon svg {
            @include square(22);
        }

        .title {
            margin-bottom: 12px;
            font-size: $fontSize-root - 3px;
            font-weight: $fontWeight-medium;
            line-height: 24px;
            letter-spacing: $letterSpacing-base;
            color: stencilColor('color-textHeading');
            text-transform: uppercase;
        }

        .shareLinkSocial__social {
            .title {
                margin-bottom: 7px;
            }

            svg {
                fill: $color-black;
            }
        }

        &.is-open {
            display: block;
        }
    }
}

.blog-thumb {
    font-size: 0;

    >div {
        font-size: $fontSize-root - 4;
        width: 50%;

        @include smin-xs {
            font-size: $fontSize-root - 2;
        }

        @include smin-m {
            font-size: $fontSize-root;
        }
    }

    .block-1 {
        padding-right: 5px;

        @include smin-xs {
            padding-right: 10px;
        }

        @include smin-m {
            padding-right: 25px;
        }
    }

    .block-2 {
        padding-left: 5px;

        @include smin-xs {
            padding-left: 10px;
        }

        @include smin-m {
            padding-left: 25px;
        }
    }
}

.blog-lasted-new {
    font-size: 0;

    img {
        object-fit: cover;
    }

    @include smin-m {
        display: grid;
        grid-template-columns: repeat(3, 1fr);
        grid-template-rows: repeat(2, 1fr);
        grid-column-gap: 30px;
        grid-row-gap: 30px;

        .item-1 { 
            grid-area: 1 / 1 / 3 / 3;

            img {
                min-height: 702px;
            }
        }

        .item-2,
        .item-3 { 
            img {
                min-height: 336px;
            }
        }

        .item-2 { 
            grid-area: 1 / 3 / 2 / 4;
        }

        .item-3 { 
            grid-area: 2 / 3 / 3 / 4;
        }
    }

    @include smax-m {
        .item-1 { 
            width: 100%;
            margin-bottom: 20px;

            img {
                min-height: 503px;
            }
        }

        .item-2,
        .item-3 { 
            width: 50%;
            display: inline-block;
            vertical-align: top;

            img {
                min-height: 220px;
            }
        }

        .item-2 { 
            padding-right: 10px;
        }

        .item-3 { 
            padding-left: 10px;
        }
    }

    @include smax-xs {
        .item { 
            width: 100%;
            padding: 0;
            margin-bottom: 30px;

            img {
                min-height: 325px;
            }

            &:last-child {
                margin-bottom: 0;
            }
        }
    }
}

.blog-post-featured {
    .item {
        padding: 10px;

        +.item {
            margin-top: 20px;
        }
    }

    .content-left {
        width: 168px;

        img {
            min-height: 172px;
            max-height: 172px;
            object-fit: cover;
        }
    }

    .content-right {
        width: calc(100% - 168px);
        padding-left: 20px;

        .name {
            font-size: $h5-font-size * 0.53846153846;
        }

        .summary {
            color: #2a2a2a;
            line-height: 1.75;
        }
    }

    @include smin-xs {
        .item {
            padding: 16px;
        }

        .content-left {
            width: 245px;

            img {
                min-height: 152px;
                max-height: 152px;
            }
        }

        .content-right {
            width: calc(100% - 245px);
            padding-left: 20px;

            .name {
                font-size: $h5-font-size * 0.76923076923;
            }

            .summary {
                color: #2a2a2a;
                line-height: 1.75;
            }
        }
    }

    @include smin-m {
        .item {
            padding: 20px;
        }

        .content-left {
            width: 364px;

            img {
                min-height: 243px;
                max-height: 243px;
            }
        }

        .content-right {
            width: calc(100% - 364px);
            padding-left: 20px;

            .name {
                font-size: $h5-font-size;
            }

            .summary {
                color: #2a2a2a;
                line-height: 1.75;
            }
        }
    }
}

.halo-blog {
    width: auto;
    font-size: 0;

    &-item {
        width: 100%;
        padding: 0 0 30px;
        margin: 0;
    }

    &-wrapper {
        padding: 20px 20px 40px;
    }

    img {
        min-height: 315px;
        max-height: 315px;
        object-fit: cover;
    }

    .title {
        margin-top: 20px;
    }

    @include smin-xs {
        margin: 0 -10px;

        &-item {
            width: 50%;
            padding: 0 10px 20px;
        }
    }

    @include smin-m {
        margin: 0 -15px;

        &-item {
            width: 33.3334%;
            padding: 0 15px 30px;
        }
    }
}

layouts/container/container.scss:
// =============================================================================
// GRID (CSS)
// =============================================================================

.container {
    margin-left: auto;
    margin-right: auto;
    max-width: 100%;
    padding-left: 10px;
    padding-right: 10px;
    position: relative;
    width: layout("fixed");

    .container {
        padding-left: 0;
        padding-right: 0;
    }

    &.container--large {
        max-width: 1670px;
        margin-left: auto;
        margin-right: auto;
    }

    &.container--medium {
        max-width: 1470px;
        margin-left: auto;
        margin-right: auto;
    }

    &.container--small {
        max-width: 1270px;
        margin-left: auto;
        margin-right: auto;
    }

    &.container-full {
        max-width: 100%;
        padding-left: 0;
        padding-right: 0;
    }

    @include smin-s {
        padding-left: 50px;
        padding-right: 50px;
    }

    @include smin-xxl {
        max-width: 1670px;

        &.container-1200 {
            max-width: 1270px;
        }

        &.container-1400 {
            max-width: 1470px;
        }

        &.container-1500 {
            max-width: 1570px;
        }

        &.container-1600 {
            max-width: 1670px;
        }

        &.container-1800 {
            max-width: 1870px;
        }
    }
}

layouts/sidebar/block.scss:
// =============================================================================
// SIDEBAR BLOCKS (CSS)
// =============================================================================


// Block
// -----------------------------------------------------------------------------
.sidebarBlock {
    padding: 0 20px 1px;
    position: relative;
    margin-bottom: 30px;

    &:last-child {
