}

layouts/brands/brandGrid.scss:
// =============================================================================
// BRAND GRID (CSS)
// =============================================================================


// Brand grid view
// -----------------------------------------------------------------------------
//
// 1. Re-set the font-size, as we're reducing it to 0 in the .brandGrid class.
//
// -----------------------------------------------------------------------------

.brandGrid {

    @include u-listBullets("none");
    @include grid-row($behavior: "nest");
    font-size: 0; // 1
    margin-bottom: spacing("single");

    .brand {
        @include grid-column(6, $float: none);
        display: inline-block;
        font-size: fontSize("base"); // 1
        vertical-align: top;

        @include breakpoint("small") {
            width: grid-calc(4, $total-columns);
        }

        @include breakpoint("medium") {
            width: grid-calc(3, $total-columns);
        }

        .card-title {
            text-align: center;
        }
    }
}

// AZ Brand Layout
// -----------------------------------------------------------------------------
.page-brands {
    .page-heading {
        margin-bottom: 32px;
    }
}

.haloAZBrandsTable {
    display: flex;
    font-size: 0;
    letter-spacing: 0;
    flex-flow: row nowrap;
    margin: 0 0 30px 0;
    overflow-x: auto;
    padding-bottom: 5px;

    li {
        display: inline-block;
        vertical-align: top;
        border: 1px solid #d6d6d6;
        border-left: none;

        a {
            font-size: $fontSize-root;
            font-weight: $fontWeight-bold;
            color: $color-textBase;
            display: block;
            height: 40px;
            min-width: 40px;
            line-height: 40px;
            text-align: center;
            text-decoration: none;
            text-transform: uppercase;
        }

        &:first-child {
            min-width: 70px;
            border-left: 1px solid #d6d6d6;
        }

        &.disable {
            pointer-events: none;

            a {
                color: $color-grey;
                pointer-events: none;
            }
        }

        &.is-active {
            border-color: $buttonStyle-primary-borderColorActive;
            background-color: $buttonStyle-primary-backgroundColorActive;

            a {
                color: $buttonStyle-primary-colorActive;
            }
        }
    }

    @media (min-width: 1025px) {
        margin: 0 0 40px 0;

        li {
            width: 10%;

            &:hover {
                border-color: $buttonStyle-primary-borderColorActive;
                background-color: $buttonStyle-primary-backgroundColorActive;

                a {
                    color: $buttonStyle-primary-colorActive;
                }
            }
        }
    }
}

.haloAZBrandsWrapper {
    .azBrands-group {
        display: none;
        margin: 0 0 30px 0;

        &-title {
            font-size: $fontSize-root + 4px;
            font-weight: $fontWeight-bold;
            color: $color-textBase;
            line-height: 35px;
            margin: 0 0 10px 0;
            padding: 0;
            text-transform: uppercase;
        }

        &-list {
            margin: 0;
            list-style: none;

            .brand-name {
                .title {
                    margin: 0;
                    font-family: $fontFamily-sans;
                    font-size: $fontSize-root;
                    color: $color-textBase;
                    line-height: 35px;
                    text-transform: capitalize;

                    .link {
                        color: $header-font-color;
                        padding: 0 0 1px 0;
                        transition: unset;
                    }
                }
            }
        }

        &.is-active {
            display: block;
        }
    }

    &.active-all {
        column-count: 2;

        .azBrands-group {
            display: block;
            break-inside: avoid;
            column-break-inside: avoid;
            page-break-inside: avoid;
            column-count: unset;
        }
    }

    &.is-loading {
        .loadingOverlay {
            display: block;
        }
    }

    @media (min-width: 768px) {
        .azBrands-group {
            column-count: 3;
        }

        &.active-all {
            column-count: 3;
        }
    }

    @media (min-width: 1025px) {
        .azBrands-group {
            column-count: 4;

            &-list {
                .brand-name {
                    .title {
                        .link {
                            &:hover {
                                color: $color-textLink-hover;
                                background-image: linear-gradient(transparent 97%, $color-textLink-hover 3%);
                                background-repeat: repeat-x;
                                background-position-y: 0;
                            }
                        }
                    }
                }
            }
        }

        &.active-all {
            column-count: 4;
        }
    }

    @media (min-width: 1280px) {
        .azBrands-group {
            column-count: 5;
        }

        &.active-all {
            column-count: 5;
        }
    }

    @media (min-width: 1600px) {
        .azBrands-group {
            column-count: 6;
        }

        &.active-all {
            column-count: 6;
        }
    }
}

layouts/brands/brand.scss:
.brand-image-container {

    @include lazy-loaded-padding("thumb_size");
    max-width: get-width(stencilString("thumb_size"));
    position: relative;

    img {
        @include lazy-loaded-img;
    }
}

.brand {
    .card-img-container {

        @include lazy-loaded-padding("brand_size");
        max-width: get-width(stencilString("brand_size"));
    }
}

layouts/products/productGrid.scss:
// =============================================================================
// PRODUCT GRID (CSS)
// =============================================================================


// Product grid view
// -----------------------------------------------------------------------------
//
// 1. Re-set the font-size, as we're reducing it to 0 in the .products class.
//
// -----------------------------------------------------------------------------

.productGrid {
    @include u-listBullets("none");
    @include grid-row($behavior: "nest");
    font-size: 0; // 1
    margin: 0 -5px;

    .product {
        @include grid-column(6, $float: none);
        padding: 0 5px;
        display: inline-block;
        font-size: fontSize("base"); // 1
        vertical-align: top;
    }

    @include smin-s{
        margin: 0 -8px;

        .product {
            padding: 0 8px;
        }
    }

    @include smin-m {
        margin: 0 -15px;

        .product {
            padding: 0 15px;
            width: grid-calc(3, $total-columns);
        }
    }
}


// Product when sidebar is present
// -----------------------------------------------------------------------------
//
// 1. When the sidebar is visible, reduce products to 3 across.
//
// -----------------------------------------------------------------------------

[data-layout="product-col1"] {
    display: block;
    margin: 0;
    font-size: 0;
    list-style: none;
    text-align: left;

    .product {
        display: inline-block;
        vertical-align: top;
        width: 100%;
        padding: 0;
        margin: 0 0 30px 0;
        white-space: normal;
    }

    .card-figure {
        .card-figcaption-bottom {
            display: none;
        }
    }

    .card-body {
        .number-review {
            top: 1px;
        }
    }

    .card-link {
        .card-img-container {
            margin: 0 auto;
            max-width: 100%;
        }
    }

    .card-desc {
        line-height: 1.2;
        max-height: calc(1.2em * 5);
        overflow: hidden;
        white-space: pre-line;
        text-overflow: clip !important;
        margin: 0 0 8px;
        color: $header-font-color;
    }

    @media (min-width: 551px) {
        .card {
            width: 100%;
            display: table;
        }

        .card-figure {
            display: table-cell;
            vertical-align: top;
            width: 250px;
        }

        .card-body {
            display: table-cell;
            vertical-align: top;
            padding: 0 0 0 30px;
            width: calc(100% - 250px);

            .card-body-content {
                padding-top: 0;
            }
        }
    }

    @include smin-s {
        .card-body {
            .card-body-button {
                justify-content: flex-start;
            }
        }
    }

    @include smin-m {
        .product {
            margin-bottom: 10px;
        }

        .card {
            .card-figure {
                &:hover {
                    .card-figure-action {
                        opacity: 1;
                        visibility: visible;
                        pointer-events: auto;
                    }
                }
            }
        }

        .card-figure {
            width: 278px;

            .card-figure-action {
                position: absolute;
                top: auto;
                left: 0;
                bottom: 0;
                display: flex;
                align-items: center;
                justify-content: center;
                opacity: 0;
                visibility: hidden;
                pointer-events: none;
                z-index: 10;
                transition: $anchor-transition;
                width: 100%;
            }

            .card-quickview,
            .card-compare {
                flex: 0 0 50%;
            }

            .card-quickview {
                +.card-compare {
                    border-left: 1px solid #dfdcd5;
                }
            }
        }

        .card-body {
            display: table-cell;
            vertical-align: top;
            padding-left: 30px;
            padding-top: 0;
            width: calc(100% - 278px);

            .card-body-button {
                margin-top: 28px;
            }
        }

        .card-option {
            .form-field {
                display: flex;
                align-items: center;
            }
        }
    }

    @include smin-l {
        .card-body {
            padding: 24px 30px;
        }
    }

    .product {
        @include smin-s {
            width: grid-calc(12, $total-columns);
        }
    }
}

[data-layout="product-col2"] {
    .product {
        @include smin-s {
            width: grid-calc(6, $total-columns);
        }
    }
}

[data-layout="product-col3"] {
    .product {
        @include smin-m {
            width: grid-calc(4, $total-columns);
        }
    }
}

[data-layout="product-col4"] {
    .product {
        @include smin-s {
            width: grid-calc(3, $total-columns);
        }
    }
}

[data-layout="product-col5"] {
    .product {
        @include smin-s {
            width: grid-calc(2, $total-columns - 2);
        }
    }
}

//
// Product Grid with masonry layout
//
// Purpose: Display products in a masonry grid, with a display inline-block for
// older browsers that don't support css multicolumn
//
// -----------------------------------------------------------------------------

.productMasonry {
    column-count: 2;
    column-fill: auto;
    column-gap: remCalc(20px);
    margin: 0 0 spacing("single");
    overflow: hidden;

    @include breakpoint("small") {
        column-count: 3;
    }

    @include breakpoint("medium") {
        column-count: 4;
    }

    .product {
        break-inside: avoid;
        display: block;
        margin-bottom: spacing("double");
        padding: 0;
        page-break-inside: avoid; // for firefox
        width: 100%;
    }

    .card {
        margin: 0;
    }
}


// Product grid view variation
// -----------------------------------------------------------------------------

.productGrid--maxCol6 {
    .no-csscolumns & {
        .product {
            @include breakpoint("medium") {
                width: grid-calc(2, $total-columns);
            }
        }
    }

    .csscolumns & {
        @include breakpoint("medium") {
            column-count: 6;
        }
    }
}

.productGrid--maxCol3 {
    .no-csscolumns & {
        .product {
            @include breakpoint("medium") {
                width: grid-calc(4, $total-columns);
            }
        }
    }

    .csscolumns & {
        @include breakpoint("medium") {
            @include grid-column(12);

            float: none;

            .product {
                @include grid-column(4, $float: none);
            }
        }
    }
}

