        padding-left: 15px;
        padding-right: 15px;
    }

    .alertBox-message {
        display: inherit;
        text-align: center;
    }

    .form-field {
        margin-bottom: 0;

        .hiddenLabels {
            display: none;
        }

        +.form-field {
            margin-top: 15px;

            &.form-field-radio {
                margin-top: 22px;
                border-bottom: none;
                padding-bottom: 0;

                @media (max-width: 767px) {
                    .form-label {
                        &:first-child {
                            display: block;
                            margin-right: 0;
                            margin-bottom: 16px;
                        }
                    }
                }
            }
        }

        +.form-field-textarea {
            margin-top: 13px;

            .form-input {
                max-height: 136px;
                line-height: 26px;
            }
        }

        +.form-field-checkbox {
            margin-top: 21px;
        }

        &.form-field-radio .form-radio+.form-label {
            color: $color-textBase;
        }
    }

    .form-label {
        margin-bottom: 10px;
    }

    .form-input {
        font-style: normal;
        padding-left: 20px;
        padding-right: 20px;
    }

    .hl-form-field-wrapper {
        +.form-field {
            margin-top: 15px;
        }
    }

    .form-field-checkbox {
        position: relative;

        input[type="checkbox"] {
            position: absolute;
            top: 5px;
            left: 0;
            width: 14px;
            height: 14px;
            border-radius: 0;
            border: 1px solid #d0d0d0;
            appearance: none;

            +.form-label {
                font-size: $fontSize-root;
                color: $header-font-color;
                padding-left: 25px;
                margin-bottom: 0;
                position: relative;
            }

            &:checked {
                background: $check-pip-backgroundImage;
                background-size: contain;
                background-repeat: no-repeat;
            }
        }

        .link {
            border-bottom: 1px solid $color-textLink;
            color: $color-textLink;
        }
    }

    .form-field-radio {
        padding-top: 0;
    }

    .form-actions {
        text-align: center;
        margin-top: 25px;

        .button {
            width: 100%;
            margin-bottom: 60px;
        }
    }
}

