// FAQs Page
// -----------------------------------------------------------------------------
.page--faqs {
    margin-top: 10px;

    @media(min-width: 768px) {
        margin-top: 45px;
    }
}

.halo-faqs {
    &-content {
        .card-header {
            position: relative;
        }

        .card-body {
            padding: 8px 20px 16px;
            text-align: left;
            background-color: transparent;

            strong {
                font-weight: $fontWeight-medium;
            }
        }

        .title {
            width: 100%;
            text-transform: unset;
            text-align: left;
            padding: 16px 20px;
            transition: $anchor-transition;

            &.collapsed {
                position: relative;
                color: $color-textLink-hover;

                +.icon-plus {
                    &:before {
                        transform: translateY(-50%) rotate(90deg);
                    }
                }
            }
        }

        .icon-plus {
            display: inline-block;
            vertical-align: top;
            font-size: 0;
            letter-spacing: 0;
            position: absolute;
            top: 50%;
            right: 20px;
            width: 16px;
            height: 16px;
            z-index: 2;
            pointer-events: none;
            transform: translateY(-50%);
            background: transparent;

            &:before,
            &:after {
                content: "";
                position: absolute;
                top: 50%;
                right: 50%;
                z-index: 2;
                transform: translate(50%, -50%);
                background: #464646;
                transition: $anchor-transition;
            }

            &:before {
                width: 2px;
                height: 16px;
            }

            &:after {
                width: 16px;
                height: 2px;
            }
        }

        .collapse {
            position: relative;
            display: none;
        }

        .faq-content {
            p {
                color: #6E6E6E;
            }

            .button {
                min-width: 190px;
            }
        }
    }
}

