        margin-bottom: 13px;
        margin-top: 0;
    }

    .halo-location-text {
        color: stencilColor("color-textBase");
        font-size: stencilNumber("fontSize-root");
        font-weight: fontWeight("normal");
        line-height: 28px;
        margin-bottom: 0;
    }
}

.halo-location-bottom {
    font-size: 0;
    margin-top: 50px;

    .HL-ColLeft {
        width: 310px;
        display: inline-block;
        vertical-align: top;
        font-size: stencilNumber("fontSize-root");

        .InfoFind {
            padding: 0 5px 0 0;
            max-height: 600px;
            overflow-y: auto;
            border: 1px solid #E6E6E6;

            &::-webkit-scrollbar {
                width: 5px;
            }

            &::-webkit-scrollbar-track {
                background: #EAEAEA;
            }

            &::-webkit-scrollbar-thumb {
                background: #000000;
            }

            .halo-location-list {

                .map-hidden {
                    display: none;
                }

                .halo_maps_elist {
                    &.map-active {
                        background-color: #E6E6E6;
                    }
                }

                >div {
                    padding: 25px;

                    +div {
                        border-top: 1px solid #E6E6E6;
                    }
                }

                .halo-location-heading {
                    color: stencilColor("color-textBase");
                    font-size: stencilNumber("fontSize-root") + 2;
                    font-weight: fontWeight("semibold");
                    cursor: pointer;
                    margin-top: 0;
                    margin-bottom: 10px;

                    span {
                        background-image: linear-gradient(currentColor, currentColor);
                        background-position: 0 90%;
                        background-repeat: no-repeat;
                        background-size: 0 1px;
                        transition: background-size .5s;
                    }

                    &:hover span {
                        background-size: 100% 1px;
                    }
                }

                .halo-location-info {
                    color: stencilColor("color-textBase");
                    font-size: stencilNumber("fontSize-root");
                    font-weight: fontWeight("normal");
                    line-height: 1.8;
                    margin-bottom: 0;
                }
            }
