    .HL-ColRight {
        width: calc(100% - 310px);
        display: inline-block;
        vertical-align: top;
        font-size: stencilNumber("fontSize-root");

        iframe {
            width: 100%;
            min-height: 600px;
        }
    }
}

@include smax-m {
