components/foundation/breadcrumbs/breadc:
// =============================================================================
// BREADCRUMBS (CSS)
// =============================================================================
//
//
// 1. Hide breadcrumbs on mobile-sized screens to improve screen real estate.
//
// 2. Since our breadcrumbs are centred with a variable width, we need to use
//    display: inline-block; on the breadcrumbs. Hence needing to remove the
//    white-space with font-size: 0;
//    The font-size is reset in the breadcrumb settings file.
//
// -----------------------------------------------------------------------------

.breadcrumbs-content {
    padding: 8px 12px;

    @include smin-s {
        padding: 12px;
    }

    @include smin-m {
        padding: 16px;
    }
}

ol.breadcrumbs {
    display: block; // 1
    font-size: 0; // 2
    overflow: visible;
    margin: 0;

    @include smax-m{
        .is-scroll & {
            display: block;
            white-space: nowrap;
            overflow: auto hidden;
            margin: 0 -10px;
            padding: 0 10px;

            .breadcrumb {
                &.is-active {
                    .breadcrumb-label {
                        padding-right: 20px;
                    }
                }
            }

            &:before {
                content: '';
                display: block;
                position: absolute;
                top: 50%;
                left: 0;
                background: linear-gradient(270deg, hsla(0, 0, 100%, 1) 0%, hsla(0, 0, 100%, .1));
                width: 12px;
                height: 20px;
                z-index: 1;
                transform: rotate(180deg) translateY(50%);
            }

            &:after {
                content: '';
                display: block;
                position: absolute;
                top: 50%;
                transform: translateY(-50%);
                right: 0;
                background: linear-gradient(270deg, hsla(0, 0, 100%, 1) 0%, hsla(0, 0, 100%, .1));
                width: 50px;
                height: 20px;
                z-index: 1;
            }

            .breadcrumb {
                float: none;
            }
        }
    }
}

.breadcrumb {
    font-size: $fontSize-root - 4;
    line-height: 1.2;
    display: inline-block;
    float: none;
    margin-right: 0.25rem;

    .breadcrumb-label {
        font-weight: $fontWeight-semibold;
        color: $color-custom-grey;
    }

    &.is-active {
        font-size: $fontSize-root - 2;

        >.breadcrumb-label {
            color: $header-font-color;
            cursor: pointer;
        }

        @include smin-m {
            font-size: $fontSize-root;
        }
    }

    &:not(:first-child):before {
        content: "";
        display: inline-block;
        vertical-align: middle;
        width: 12px;
        height: 12px;
        margin: 0 11px;
