components/foundation/modal/modal.scss:
// =============================================================================
// MODAL (CSS)
//
// // 1. Fix for content shifted to top in modal window when bottom variant option selected
// // 2. Icon styles and icon animations moved from the removed "sweetalert2" library
// =============================================================================


.modal-background {
    z-index: 999;
    background-color: rgba(0, 0, 0, .4);
    backdrop-filter: blur(10px);

    .openQuickSearch & {
        z-index: 1010;
    }
}

.modal {
    left: 50%;
    margin: 0;
    max-height: 90%;
    max-width: 95%;
    min-height: 240px;
    outline: none;
    overflow: hidden;
    padding: 0;
    top: 50% !important;
    transform: translate(-50%, -50%);
    z-index: 1000;

    .openQuickSearch & {
        z-index: 1010;
    }

    .form-field {
        position: relative; // 1
    }

    &.modal--standard {
        width: 100%;
    }

    &.modal--editOptions {
        .modal-body {
            padding: 0 0 140px 0;
            overflow-x: hidden;
        }
    }

    @media (min-width: 1025px) {
        width: 100%;
        max-width: 990px;
    }
}

.modal--large {
    @include smin-xl {
        width: $modal-width--large;
        max-width: $modal-width--large;
    }
}

.modal-header {
    padding-left: $reveal-modal-paddingHorizontal;
    padding-right: remCalc($reveal-close-height) + $reveal-close-side;
}

.modal-header-title {
    font-size: 20px;
    text-align: center;
}

.modal-close {
    @include square($reveal-close-height);

    @include addFocusTooltip;
    color: stencilColor("icon-color");
    font-size: 26px;
    line-height: remCalc($reveal-close-height);
    padding: 0;
    position: absolute;
    text-align: center;
    text-decoration: none;
    z-index: zIndex("high");

    @include breakpoint("medium") {
        font-size: 32px;
    }

    &:hover {
        color: stencilColor("icon-color-hover");
    }

    &::before {
        right: 50%;
        transform: translateX(50%);
    }

    &::after {
        right: 50%;
        transform: translateX(remCalc(10px));
    }
}

.modal-body {
    overflow: auto;
    -webkit-overflow-scrolling: touch;
    padding: 10px;

    @include smin-s {
        padding: 20px;
    }

    @include smin-m {
        padding: 30px;
    }
}

.modal--alert {
    background-color: $alert-modal-bgColor;
    border-radius: $alert-modal-borderRadius;
    font-family: $alert-font-body;
    padding: 40px 20px 20px;
    text-align: center;

    .alert-icon { // 2
        border: 0.25em solid transparent;
        border-radius: 50%;
        box-sizing: content-box;
        cursor: default;
        display: flex;
        font-family: inherit;
        height: 5em;
        justify-content: center;
        line-height: 5em;
        margin: 1.25em auto 1.875em;
        position: relative;
        user-select: none;
        width: 5em;
    }

    .error-icon {
        animation: animate-icon 0.5s;
        border-color: #f27474;
        color: #f27474;

        .icon-content {
            animation: animate-icon-content 0.5s;
            flex-grow: 1;
            position: relative;

            .line {
                background-color: #f27474;
                border-radius: 0.125em;
                display: block;
                height: 0.3125em;
                position: absolute;
                top: 2.3125em;
                width: 2.9375em;

                &-left {
                    left: 1.0625em;
                    transform: rotate(45deg);
                }

                &-right {
                    right: 1em;
                    transform: rotate(-45deg);
                }
            }
        }
    }

    .warning-icon { 
        animation: animate-icon 0.5s;
        border-color: #facea8;
        color: #f8bb86;

        .icon-content {
            align-items: center;
            animation: animate-icon-content 0.5s;
            display: flex;
            font-size: 3.75em;
        }
    }

    .modal-content {
        color: $alert-font-bodyColor;
        font-size: $alert-font-bodySize;
    }

    .button-container {
        margin: $alert-button-wrapperMargin;

        .confirm {
            background-color: $alert-button-bgColor;
            border-color: $alert-button-borderColor;
            color: $alert-button-color;
        }

        .confirm:focus,
        .confirm:hover {
            background-color: $alert-button-bgColorHover;
            border-color: $alert-button-borderColorHover;
            color: $alert-button-colorHover;
        }

        .confirm:active {
            background-color: $alert-button-bgColorActive;
            border-color: $alert-button-borderColorActive;
            color: $alert-button-colorActive;
        }
    }
}

.hide-content {
    opacity: 0;
}

//Icon animations
@keyframes animate-icon { // 2
    0% {
        opacity: 0;
        transform: rotateX(100deg);
    }

    100% {
        opacity: 1;
        transform: rotateX(0deg);
    }
}

@keyframes animate-icon-content {
    0% {
        margin-top: 1.625em;
        opacity: 0;
        transform: scale(0.4);
    }

    50% {
        margin-top: 1.625em;
        opacity: 0;
        transform: scale(0.4);
    }

    80% {
        margin-top: -0.375em;
        transform: scale(1.15);
    }

    100% {
        margin-top: 0;
        opacity: 1;
        transform: scale(1);
    }
}

components/foundation/pagination/paginat:
// =============================================================================
// PAGINATION (CSS)
// =============================================================================


// Pagination list items
// -----------------------------------------------------------------------------
//
// 1. Centred list via inline-block. Font-size: 0; to remove white-space.
// 2. Specificity overrides for Foundation's inadequate selectors.
//
// -----------------------------------------------------------------------------

.pagination-list {
    @include clearfix;
    font-size: 0; // 1
    margin-top: 30px;
    margin-bottom: 0;
    text-align: center;

    .pagination-item { // 2
        display: inline-block;
        vertical-align: middle;
        margin: 0 16px;
    }

    .page-type-blog & {
        margin-top: 10px;
    }
}


// Pagination list items
// -----------------------------------------------------------------------------
//
// 1. Specificity overrides for Foundation's inadequate selectors.
//
// -----------------------------------------------------------------------------

.pagination-item {
    line-height: 1;
    padding: 12px 0;

    .pagination-link {
        display: flex;
        @include square(32);
        text-decoration: none;
        transition: $pagination-link-transition;

        &:hover {
            color: $pagination-link-color-hover;
        }
    }

    &.pagination-item--current { // 1
        border-color: $pagination-link-current-borderColor;
        border-radius: $pagination-link-current-borderRadius;
        margin: $pagination-link-current-margin;
        pointer-events: none;

        > a {
            background-color: $pagination-link-current-background;
        }
    }

    &.pagination-item--previous, // 1
    &.pagination-item--next { // 1
        padding: 0;

        &.button--disable {
            cursor: not-allowed;

            .pagination-link {
                pointer-events: none;
            }
        }

        > a {
            border: $pagination-item-border;
            width: 80px;
            height: 56px;
            border-radius: 60px;
            color: $pagination-item-previousNext-color;
            padding: 1px 0 0;

            .icon,
            .icon svg {
                @include square(40);
            }

            &:hover {
                color: $pagination-item-previousNext-color-hover;

                .icon > svg {
                    fill: $pagination-item-previousNext-color-hover;
                }
            }
        }

        @include smax-xs {
            display: none;
        }
    }
}

.pagination-item--previous,
.pagination-item--next {
    .icon {
        height: remCalc(20px);
        margin-top: -(remCalc(2px));
        width: remCalc(20px);
    }
}

.pagination-item--previous {
    float: left;
}

.pagination-item--next {
    float: right;
}


// Simple/small pagination (Next/Previous arrows are the only way to navigate)
// -----------------------------------------------------------------------------

.pagination-list--small {
    position: relative;

    .pagination-item {
        color: $pagination-list--small-color;
        cursor: inherit;
        line-height: $pagination-list--small-lineHeight;
    }

    .pagination-link {
        padding: $pagination-list--small-link-padding;
    }

    .pagination-item--previous,
    .pagination-item--next {
        position: absolute;
        top: 0;
    }

    .pagination-item--previous {
        left: 0;
    }

    .pagination-item--next {
        right: 0;
    }
}

components/foundation/panels/panels.scss:
// =============================================================================
// PANELS (CSS)
// =============================================================================


.panel-title {
    font-size: fontSize("small");
}

.panel--large {
    .panel-body {
        margin-bottom: spacing("single") + spacing("third");
        padding: 10px;

        @include smin-xs {
            padding: spacing("single") + spacing("third");
        }
    }
}

components/foundation/tabs/tabs.scss:
// =============================================================================
// TABS (CSS)
// =============================================================================
//
// Most of these styles deliberately reset Citadel's defaults for smaller
// screens since the styles in Stencil for smaller screens are more persistent
// across breakpoints.
//
// -----------------------------------------------------------------------------

.tab {
    margin: 0 32px 20px 0;
    float: none;

    @include smin-s {
        float: left;
    }

    &.is-active {
        .tab-title {
            background-color: $color-white;
        }
    }
}

.tab-title {
    font-size: $fontSize-root + 4;
    font-weight: $fontWeight-semibold;
    padding: 14px 16px;
    border-radius: 10px;

    @include smax-xxl {
        font-size: $fontSize-root;
    }
}

.tab-content {
    &.has-jsContent {
        display: block;
        height: 0;
        overflow: hidden;
        padding: 0;
        visibility: hidden;

        &.is-active {
            height: auto;
            overflow: visible;
            padding: $tabs-content-padding;
            visibility: visible;
        }
    }

    &.is-active {
        display: block;

        .toggle-content {
            display: block;
        }
    }

    //
    // Product review displays in tabs
    //                                                                                                                                                           
    // Purpose: Display product reviews within tabbed content on product pages.
    // -----------------------------------------------------------------------------
    .productReview {
        @include breakpoint("small") {
            width: grid-calc(6, $total-columns);
        }

        @include smin-m {
            width: grid-calc(4, $total-columns);
        }

        @include breakpoint("large") {
            width: grid-calc(6, $total-columns);
        }
    }

    .productReviews {
        border-top: 0;
    }
}

.productView-description {
    table {
        width: 100%;
        border-collapse: collapse;
        border-spacing: 0;
        table-layout: auto;
        margin: 0;

        tr {
            background: transparent;
            height: 45px;

            &:nth-child(odd) {
                td:first-child {
                    border-radius: 10px 0 0 10px;
                    background: $body-bg;
                }

                td:last-child {
                    border-radius: 0 10px 10px 0;
                    background: $body-bg;
                }
            }
        }

        td {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            line-height: 1.75;
            padding: 11px 14px;
            text-align: left;
            color: $color-textBase;

            &.tb-title {
                color: stencilColor(color-textHeading);
                font-weight: $fontWeight-medium;
                min-width: 200px;
            }
        }
    }

    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
        line-height: 1.25;
        margin-top: 0;
        margin-bottom: 16px;
    }

    ul {
        list-style: none;
    }

    .tab-content {
        background-color: $color-white;
        line-height: 1.75;
    }

    @include smin-s {
        .tab-content {
            padding: 20px;
            transition: $anchor-transition;
        }

        .toggle-title {
            display: none;
            margin: 0;
            padding: 0;
        }
    }

    @include smin-m {
        .tab-content {
            padding: 45px;
        }
    }

    @include smax-s {
        .tabs {
            display: none;
            margin-left: 0;
            margin-bottom: 0;
            list-style: none;
            padding-bottom: 0;
            border-bottom: 0;

            .tab {
                display: block;
                float: none;
                width: 100%;
            }

            .tab-title {
                display: block;
                width: 100%;
                padding: 0 15px;
                text-align: left;
            }
        }

        .tabs-contents {
            margin: 0;

            .tab-content {
                display: block;
                padding: 0;
                margin: 0 0 16px;
                transition: $anchor-transition;

                &#tab-reviews {
                    .toggleLink {
                        padding-bottom: 10px;
                    }

                    .icon {
                        width: 17px;
                        height: 18px;
                    }
                }
            }

            .toggle-title {
                display: block;
                margin: 0;
                padding: 0 0 16px;
            }

            .toggleLink {
                display: block;
                float: none;
                width: 100%;
                padding: 16px 10px 0;
                position: relative;
                font-weight: $fontWeight-semibold;
                text-transform: uppercase;
                transition: $anchor-transition;

                .text {
                    padding-bottom: 0;
                    background-image: unset;
                }

                .icon-plus {
                    font-size: 0;
                    display: inline-block;
                    vertical-align: top;
                    position: absolute;
                    top: calc(50% + 8px);
                    right: 0;
                    z-index: 2;
                    pointer-events: none;
                    transform: translateY(-50%);
                    background-color: transparent;
                    background-position: unset;
                    background-repeat: unset;
                    background-image: unset;
                    transition: $anchor-transition;

                    &:before,
                    &:after {
                        content: "";
                        position: absolute;
                        top: 50%;
                        z-index: 2;
                        transition: $anchor-transition;
                        transform: translate(0, -50%);
                        background: $color-textBase;
                    }

                    &:before {
                        right: 19px;
                        width: 2px;
                        height: 20px;
                    }

                    &:after {
                        right: 10px;
                        width: 20px;
                        height: 2px;
                    }
                }

                &.is-open {
                    padding-bottom: 0;
                    color: $color-textBase;

                    .icon-plus {

                        &:before,
                        &:after {
                            background: $color-textBase;
                        }

                        &:before {
                            transform: translateY(-50%) rotate(90deg);
                        }
                    }
                }

                @include smin-s {
                    padding: 18px 45px 18px 0;
                }
            }

            .toggle-content {
                position: relative;

                &.is-open {
                    border-top: 1px solid #a7a7a7;
                    padding: 10px;
                }
            }
        }
    }
}

.return,
.shipping {
    margin-top: 0;
}

components/foundation/buttons/buttons.sc:
// =============================================================================
// BUTTONS (CSS)
// =============================================================================

.button {
    transition: $anchor-transition;
    font-size: $fontSize-root - 4;
    font-weight: $fontWeight-semibold;
    font-family: $body-font-family;
    min-height: 36px;
    line-height: 34px;
    padding: 0 10px;
    border-radius: 50px;
    overflow: hidden;

    // span {
    //     display: inline-block;
    //     transition: all .3s;
    // }

    // &:after {
    //     content: attr(data-button-text);
    //     transition: all .3s;
    //     position: absolute;
    //     display: flex;
    //     align-items: center;
    //     justify-content: center;
    //     width: 100%;
    //     left: 50%;
    //     top: 150%;
    //     transform: translate(-50%);
    //     opacity: 0;
    // }

    + .button {
        margin-left: 0;

        @include breakpoint("medium") {
            margin-left: spacing("half");
        }
    }

    &.button--secondary {
        color: $buttonStyle-default-color;
        background-color: $buttonStyle-default-backgroundColor;
        border-color: $buttonStyle-default-borderColor;

        &:active {
            color: $buttonStyle-default-colorActive;
            border-color: $buttonStyle-default-borderColorActive;
            background-color: $buttonStyle-default-backgroundColorActive;
        }
    }

    &.button--tertiary {
        min-height: 36px;
        line-height: 34px;
        color: $buttonStyle-tertiary-color;
        background-color: $buttonStyle-tertiary-backgroundColor;
        border-color: $buttonStyle-tertiary-borderColor;

        &:active {
            color: $buttonStyle-tertiary-colorActive;
            border-color: $buttonStyle-tertiary-borderColorActive;
            background-color: $buttonStyle-tertiary-backgroundColorActive;
        }

        @include smin-s {
            min-height: 50px;
            line-height: 48px;
        }

        @include smin-m {
            min-height: 57px;
            line-height: 55px;
        }
    }

    &.button--transparent {
        background-color: transparent;
    }

    &.loading {
        pointer-events: none;
        font-size: 0;

        svg {
            display: none;
        }

        &:before {
            content: '';
            display: inline-block;
            vertical-align: middle;
            width: 30px;
            height: 30px;
            border: 2px solid #c5c5cf;
            border-radius: 100%;
            border-top: 2px solid transparent;
            -webkit-animation: load-animate infinite linear 1s;
            animation: load-animate infinite linear 1s;
        }
    }

    &.disable,
    &[disabled] {
        color: $buttonStyle-disabled-color;
        background-color: $buttonStyle-disabled-backgroundColor;
        border-color: $buttonStyle-disabled-borderColor;
    }

    @include smin-xs {
        font-size: $fontSize-root - 2;
        border-radius: 100px;
    }

    @include smin-m {
        min-height: 46px;
        line-height: 44px;
        font-size: $fontSize-root;

        &:hover,
        &:focus {
            svg {
                fill: $buttonStyle-primary-colorHover;
            }
        }

        // &:hover {
        //     span {
        //         transform: translateY(-200%);
        //         opacity: 0;
        //     }

        //     &:after {
        //         opacity: 1;
        //         top: 50%;
        //         transform: translate(-50%, -50%);
        //     }
        // }

        &.button--secondary {
            &:hover,
            &:focus {
                color: $buttonStyle-default-colorHover;
                border-color: $buttonStyle-default-borderColorHover;
                background-color: $buttonStyle-default-backgroundColorHover;

                svg {
                    fill: $buttonStyle-default-colorHover;
                }
            }
        }

        &.button--tertiary {
            &:hover,
            &:focus {
                color: $buttonStyle-tertiary-colorHover;
                border-color: $buttonStyle-tertiary-borderColorHover;
                background-color: $buttonStyle-tertiary-backgroundColorHover;

                svg {
                    fill: $buttonStyle-tertiary-colorHover;
                }
            }
        }

        &.button--transparent {
            &:hover,
            &:focus {
                color: inherit;
            }
        }

        &.button--iconCustom {
            &:hover,
            &:focus {
                background-color: transparent;
                border-color: transparent;
                color: inherit;
            }
        }

        &.disable,
        &[disabled] {
            &:hover,
            &:focus {
                color: $buttonStyle-disabled-color;
                background-color: $buttonStyle-disabled-backgroundColor;
                border-color: $buttonStyle-disabled-borderColor;

                svg {
                    fill: $buttonStyle-disabled-color;
                }
            }
        }
    }
}

.button[disabled] {
    cursor: not-allowed;
}

.button--icon,
.button--iconCustom {
    svg {
        fill: stencilColor("button--icon-svg-color");
    }
}

.button--iconCustom {
    min-height: 40px;
    margin: 0;
    padding: 0;
    line-height: 1;

    &.button {
        background-color: transparent;
        border: none;
    }

    svg {
        width: 30px;
        height: 30px;

        @include smin-xs {
            width: 40px;
            height: 40px;
        }

        @include smax-xs {
            vertical-align: bottom;
        }
    }

    @include smin-xs {
        +.button--iconCustom {
            margin: 0 0 0 10px;
        }
    }

    @include smin-m {
        .form-option-tooltip {
            display: block;
            font-size: $fontSize-root - 2;
            line-height: 1.5;
            color: $color-white;
            opacity: 0;
            visibility: hidden;
            pointer-events: none;
        }

        &:hover {
            .form-option-tooltip {
                opacity: 1;
                visibility: visible;
                pointer-events: auto;
            }

            svg {
                animation: animateIcon .5s infinite ease;
            }
        }
    }

    @include smin-xs {
        line-height: 40px;
    }

    @include smin-hd {
        +.button--iconCustom {
            margin-left: 16px;
        }
    }
}

.button--small {
    font-size: $fontSize-root - 4;
    min-width: 95px;
    min-height: 36px;
    line-height: 34px;

    @include smin-s {
        font-size: $fontSize-root;
        min-width: 126px;
        min-height: 50px;
        line-height: 48px;
    }

    @include smin-m {
        font-size: $fontSize-root + 2;
        min-width: 147px;
        min-height: 67px;
        line-height: 65px;
    }
}

.button--large {
    font-weight: $fontWeight-normal;
    padding: 0 10px;
    min-width: 99px;
    min-height: 39px;
    line-height: 37px;

    @include smin-s {
        min-width: 125px;
        min-height: 54px;
        line-height: 52px;
    }

    @include smin-m {
        min-width: 150px;
        min-height: 68px;
        line-height: 66px;
    }
}

components/foundation/accordion/accordio:
// =============================================================================
// ACCORDION (CSS)
// =============================================================================

.accordion {
    box-shadow: none;
    border: 0;
    margin: 0;
}

.accordion-title {
    font-size: $h6-font-size;
    color: $color-textBase;
    text-transform: unset;
}

.accordion-content {
    padding: 0;
    border: 0;
    margin: 16px 0 32px 0;;
}

.accordion-navigation {
    position: relative;
    text-decoration: none;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    margin: 0;
    padding: 0;
    border: none;

    .accordion-title {
        padding: 16px 0;
    }

    svg {
        @include square(24);
        position: relative;
        top: -1px;
    }
}

// Accordion nav list
// -----------------------------------------------------------------------------
//
// Purpose: Used as navigation menu, for example: faceted search menu.
//
// -----------------------------------------------------------------------------

.accordion--navList {
    border: 0;
    border-radius: 0;

    .accordion-navigation {
        border: 0;
        border-radius: 0;
        margin: 0;
        padding: 0;

        .accordion-navigation-actions {
            position: absolute;
            top: 0;
            right: 0;
            font-size: 0;

            .icon {
                vertical-align: top;
                width: 20px;
                height: 20px;
                stroke: transparent;
                background: transparent;
            }
        }
    }

    .accordion-content {
        .navList-item {
            a {
                font-size: $fontSize-root;
                margin: 0;
                padding: 0 0 0 28px;

                .navList-action-close {
                    position: absolute;
                    float: unset;
                    right: 0;
                    top: 50%;
                    transform: translateY(-50%);
                    margin: 0;
                    width: 20px;
                    height: 20px;
                    padding: 2px;

                    .icon {
                        width: 100%;
                        height: 100%;
                        fill: $color-white;
                        stroke: $color-white;
                    }
                }

                &:before {
                    background-color: white;
                    border: 1px solid #d0d0d0;
                    border-radius: 2px;
                    @include square(20);
                    top: calc(50% - 1px);
                    bottom: auto;
                    left: 0;
                    margin: 0;
                    transform: translateY(-50%);
                    z-index: 11;
                }

                &:after {
                    left: 4px;
                    width: 12px;
                    height: 14px;
                    background-image: url("data:image/svg+xml,%3Csvg aria-hidden='true' focusable='false' data-prefix='far' data-icon='check' role='img' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 512 512' class='svg-inline--fa fa-check fa-w-16 fa-3x'%3E%3Cpath fill='%23ffffff' d='M435.848 83.466L172.804 346.51l-96.652-96.652c-4.686-4.686-12.284-4.686-16.971 0l-28.284 28.284c-4.686 4.686-4.686 12.284 0 16.971l133.421 133.421c4.686 4.686 12.284 4.686 16.971 0l299.813-299.813c4.686-4.686 4.686-12.284 0-16.971l-28.284-28.284c-4.686-4.686-12.284-4.686-16.97 0z' class=''%3E%3C/path%3E%3C/svg%3E");
                    background-repeat: no-repeat;
                    background-position: center center;
                    background-size: 100%;
                    z-index: 12;
                }

                &.is-selected {
                    &:before {
                        border-color: stencilColor('global_bg');
                        background: stencilColor('global_bg');
                    }
                }
            }

            +.navList-item {
                margin-top: 16px;
            }
        }
    }
}

components/foundation/inlineList/inlineL:
// =============================================================================
// INLINE LIST (CSS)
// =============================================================================
// 1. It prevents from missing top outline on refine filters on focus;
//
// -----------------------------------------------------------------------------

ul.inlineList { 
    @include clearfix; // 1
    overflow: visible;
    margin: 0;
    font-size: 0;
}

.inlineList--labels {
    > li {
        float: none;
        display: inline-block;
        margin-right: 16px;
        margin-bottom: 16px;
        margin-left: 0;
    }
}

@mixin lazy-loaded-img() {
    bottom: 0;
    left: 0;
    margin: auto;
    position: absolute;
    right: 0;
    top: 0;
}

@mixin lazy-loaded-padding($size-param) {
    &::after {
        content: "";
        display: block;
        height: 0;
        padding-bottom: get-padding(stencilString($size-param));
        width: 100%;
    }
}

@mixin cart-item-lazy-load-img-placeholder() {
    &::after {
        content: "";
        display: block;
        height: 100%;
        left: 0;
        position: absolute;
        top: 0;
        width: 100%;
    }
}

components/foundation/alerts/alerts.scss:
// =============================================================================
// ALERTS
// =============================================================================

.alertBox {
    border-radius: $alert-radius;
    color: stencilColor("alert-color");
}

.alertBox-icon {
    .icon {
        @include square(25px);
    }
}

.alertBox--info {
    background-color: stencilColor("banner--default-backgroundColor");
    color: stencilColor("alert-color-alt");

    .alertBox-close {
        svg {
            fill: $color-infoLight;
        }
    }
}

components/citadel/cards/cards.scss:
// =============================================================================
// CARDS (CSS)
// =============================================================================
//
// 1. Allow users to click the anchor tag on the image in the figcaption empty
//    space. This provides a much larger and simpler hit state for navigating
//    to the product details page. (Does not work in IE10 or earlier).
// 2. Using translate3d to counter a chrome specific bug: When using a masonry
//    styled layout via CSS column-count etc, transforms applied to descendants
//    are made invisible, except for 3d transforms...
//
// -----------------------------------------------------------------------------

.card {
    margin-bottom: 8px;

    &:focus-within {
        .card-figcaption {
            opacity: 1;
        }
    }

    &.focus-within {
        .card-figcaption {
            opacity: 1;
        }
    }

    @include smin-m {
        &:hover {
            .item-action {
                transform: scale(1);
            }

            .card-figcaption-button {
                bottom: 12px;
                animation: halo-fadeInUp .3s cubic-bezier(0, 0, .3, 1) forwards;
            }
        }
    }
}

.item-action {
    width: 44px;
    height: 44px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: $color-white;
    border-radius: 50%;
    transform: scale(0);
    box-shadow: -2px -2px 5px 0px rgba(0, 0, 0, 0.05), 2px 2px 5px 0px rgba(0, 0, 0, 0.05);;
    transition: $anchor-transition;

    &.is-check {
        background-color: $buttonStyle-primary-backgroundColor;

        .icon {
            fill: $buttonStyle-primary-color;
        }
    }

    @include smin-m {
        &:hover {
            animation: animateIcon .5s infinite ease;
        }
    }
}

.card-figure {
    margin-top: $card-figure-marginTop;
    overflow: hidden;
    position: relative;
    border-radius: 16px 16px 0 0;

    &:hover {
        .card-figcaption {
            opacity: 1;
        }
    }

    &__link {
        display: block;
        position: relative;
    }
}

.card-img-container {
    @include lazy-loaded-padding("productgallery_size");
}

.card-figcaption {
    margin: $card-figcaption-margin;
    pointer-events: none; // 1
    transition: opacity .8s ease;
    z-index: 10;

    @include smax-m {
        opacity: 1;

        .item-action {
            transform: scale(1);
        }

        .card-figcaption-button {
            bottom: 6px;
            min-height: 50px;
            line-height: 48px;
        }
    }

    @include smax-s {
        .card-figcaption-button {
            min-height: 36px;
            line-height: 34px;
        }
    }
}

.card-figcaption-mb {
    padding: 10px 10px 0;
}

.card-figcaption-body {
    padding: 12px;
}

.card-image {
    @include lazy-loaded-img;
    border: 0;
    /* Object-fit polyfill */
    font-family: "object-fit: contain;";
    max-height: 100%;
    object-fit: contain;
    height: 100%;
    width: 100%;
    opacity: 1;
    backface-visibility: visible;
    transition: opacity 0.5s ease-in-out;

    .card-hover & {
        opacity: 0;

        &.active {
            opacity: 1;
        }
    }
}

.hover-sections {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    display: flex;
}

.hover-section {
    flex: 1;
    height: 100%;
}

.card-title {
    color: $card-title-color;
    display: block;
    text-decoration: $card-title-textDecoration;

    &:hover {
        color: $card-title-color-hover;
    }
}

.card-text {
    margin-bottom: $card-text-marginBottom;

    abbr {
        border: 0;
        color: inherit;
        font-size: inherit;
    }
}

.card-figcaption-button {
    margin: 0 10px;
    width: calc(100% - 20px);

    @include smin-m {
        margin: 0 12px;
        width: calc(100% - 24px);
    }
}

.card-body {
    text-align: left;
    padding: 0 8px 8px;
    border-radius:  0 0 16px 16px;

    @include smin-s {
        padding: 0 12px 12px;
    }

    @include smin-m {
        padding: 8px 16px 16px;
    }
}

[data-test-info-type="price"] {
    @include smax-xxl {
        margin: 10px 0;

        .price-section {
            display: inline-block;
        }
    }
}

.price-section {
    display: inline-block;
    vertical-align: middle;
    margin-right: 5px;

    &.price-section--withoutTax,
    &.price-section--withTax {
        color: stencilColor('sale_price_color');
    }

    &.non-sale-price--withoutTax,
    &.non-sale-price--withTax {
        color: $color-textSecondary;

        .price {
            font-weight: $fontWeight-normal;
            font-size: $fontSize-root - 2;
        }
    }

    &.price-none {
        +.price-section--withoutTax {
            color: $header-font-color;
        }

        +.price-section--withTax {
            color: $header-font-color;
        }
    }
}

.card-wrapper {
    .card-figure {
        width: 58px;

        @include smin-xs {
            width: 87px;
        }

        @include smin-m {
            width: 150px;
        }
    }

    .card-body {
        padding: 0 0 0 16px;
        width: calc(100% - 58px);

        @include smin-xs {
            width: calc(100% - 87px);
        }

        @include smin-m {
            width: calc(100% - 150px);
        }
    }

    .card-title {
        font-weight: $fontWeight-semibold;
    }

    +.card-wrapper {
        margin-top: 20px;
    }

    @include smax-xxl {
        display: initial;

        .card-text {
            text-align: left;
        }
    }
}

.form-option-variant {
    position: relative;
    margin: 0;
    padding: 0;
    border-radius: 50%;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
    border: none;
    box-shadow: 1px 1px #fff;

    .form-option-swatch & {
        width: 20px;
        height: 20px;
    }

    .form-option-variant-wrapper {
        display: flex;
        justify-content: center;
        align-items: center;
        overflow: hidden;
        width: 26px;
        height: 26px;
        border-radius: 50%;

        span {
            width: 100%;
            height: 100%;
            min-width: 14px;
        }
    }
}

.form-option-tooltip {
    @include smin-m {
        display: none;
        color: $color-white;
        text-align: center;
        background: stencilColor("color-textBase");
        position: absolute;
        bottom: 100%;
        left: 50%;
        padding: 4px 7px;
        z-index: 2;
        min-width: 40px;
        transform: translateX(-50%);
        margin: 0 0 10px 0;
        transition: $anchor-transition;
        white-space: nowrap;

        &.text {
            left: 42%;
        }

        &:before {
            content: " ";
            position: absolute;
            bottom: -6px;
            left: 50%;
            height: 0;
            width: 0;
            border-top: 7px solid stencilColor("color-textBase");
            border-left: 7px solid transparent;
            border-right: 7px solid transparent;
            transform: translateX(-50%);
        }
    }
}

.card-option {
    min-height: 22px;
}

.halo-options {
    display: block;
    margin: 0;
    @include clearfix;

    &:last-of-type {
        margin-right: 0;
    }

    .form-option-swatch {
        padding: 0;
        border-radius: 50%;
    }

    .showmore {
        font-size: $fontSize-root - 2;
        display: inline-block;
        vertical-align: middle;
        margin: 6px 0 0 7px;
        height: 20px;
        line-height: 1.5;
        color: #2a2a2a;
    }

    .form-option {
        vertical-align: top;
        border: 1px solid #757575;
        margin: 0 5px 0 0;
        position: relative;
        text-align: center;
        font-weight: $fontWeight-normal;

        &:not(.form-option-swatch) {
            border-radius: 50px;
            padding: 6px 10px;
            min-width: 50px;

            @include smin-xs {
                border-radius: 100px;
            }
        }

        &.active {
            .form-option-variant {
                &:before {
                    content: "";
                    position: absolute;
                    top: -2px;
                    left: -2px;
                    width: 24px;
                    height: 24px;
                    z-index: 2;
                    border-radius: 17px;
                    border: 1px solid $color-black;
                    transition: $anchor-transition;
                }
            }
        }

        @include smin-s {
            &:not(.form-option-swatch) {
                padding: 10px;
                min-width: 43px;
            }
        }

        @include smin-m {
            margin: 0 8px 0 0;

            &:not(.form-option-swatch) {
                padding: 14px 24px;
                min-width: 50px;
            }

            .form-option-tooltip {
                display: block;
                opacity: 0;
                visibility: hidden;
                pointer-events: none;
            }

            &:hover {
                .form-option-tooltip {
                    opacity: 1;
                    visibility: visible;
                    pointer-events: auto;
                }
            }
        }
    }
}

.card-desc {
    display: none;
}

.card-action {
    @include smax-xxl {
        padding-top: 10px;
    }
}

components/citadel/forms/forms.scss:
// =============================================================================
// FORMS (CSS)
// =============================================================================


// Form variants
// -----------------------------------------------------------------------------

.form--hiddenLabels {
    .form-label {
        @include u-hidden("visually");
    }
}


// Citadel inline messages
// -----------------------------------------------------------------------------

.form-inlineMessage {
    font-size: $fontSize-root;
    display: block;
}


// Citadel field states
// -----------------------------------------------------------------------------

.form-field--error {
    .form-input {
        float: none;
    }

    .form-inlineMessage {
        &::before {
            background: $form-error-icon no-repeat;
            background-size: $form-inlineMessage-backgroundSize;
            content: "";
            display: inline-block;
            height: $form-error-icon-height;
            left: $form-error-icon-left;
            margin-right: $form-error-icon-marginRight;
            position: relative;
            top: $form-error-icon-top;
            width: $form-error-icon-width;
        }
    }
}

.form-field--success {
    .form-input[type="text"] {
        padding-right: $form-success-input-paddingRight;
        background: {
            image: $form-success-icon;
            position: right $form-success-icon-right center;
            repeat: no-repeat;
        }
    }

    .form-input[name="credit_card_number"] {
        background-position: right $form-success-icon-check-right center;
    }

    .form-input[name="cvv"] {
        background-position: right $form-success-icon-check-right center;
    }
}


// Citadel checkbox/radio positioning
// -----------------------------------------------------------------------------

.form-checkbox,
.form-radio {
    + .form-label {
        &:last-child {
            display: block;
            margin-bottom: $form-checkRadio-label-last-marginBottom;
        }

        &::after {
            top: $form-checkRadio-labelAfter-top;
        }

        &::before {
            top: $form-checkRadio-labelBefore-top;
        }
    }
}

.form-option-wrapper {
    display: inline-block;
    position: relative;

    .form-radio,
    .form-checkbox {
        bottom: 0.5rem;
        left: 0.5rem;
    }
}

// Citadel form-actions
// -----------------------------------------------------------------------------

.form-actions {
    @include grid-row();
    display: block;
    text-align: center;

    @include smin-xs {
        margin-top: spacing("single");
    }

    .button,
    input,
    a {
        vertical-align: top;
        width: 100%;

        @include smin-xs {
            margin-bottom: 0;
            width: auto;
        }

        + .button,
        + input,
        + a {
            margin-top: spacing("half");

            @include smin-xs {
                margin: 0 0 0 spacing("half");
            }
        }
    }
}

// Stencil form additions
// -----------------------------------------------------------------------------
//
// 1. Order has been placed here so that if a form error is shown, it will
//    appear before the button.
//
// -----------------------------------------------------------------------------
@mixin placeholder {
    &::-webkit-input-placeholder {@content; }
    &::-moz-placeholder {@content; }
    &:-ms-input-placeholder {@content; }
}

.form-label--alternate {
    font-size: $fontSize-root - 2;
    margin-bottom: 16px;
    line-height: 1.556;

    @include smin-xs {
        font-size: $fontSize-root;
    }

    @include smin-m {
        font-size: $fontSize-root + 2;
    }
}

.form-select--date {
    width: initial;

    + .form-select--date {
        margin-left: 16px;
    }
}

.form-select--small,
.form-select--date {
    background-position: right 0.5rem top 1rem;
    background-size: remCalc(18px);
    font-size: $input-small-fontSize;
    height: $input-small-height;
    line-height: 1rem;
    max-width: 20rem;
    padding: $input-small-padding;
    padding-right: $select-small-paddingRight;
}

.form-select--short {
    max-width: 6rem;
    width: 4.5rem;
}

.form-file {
    margin-bottom: spacing("third");
    font-size: $fontSize-root - 2;
}

.form-fileDescription {
    color: color("greys", "light");
    font-size: $fontSize-root - 2;
}

// This component should live inside a .form-row
.form-column {
    @include grid-column(12);

    @include smin-xs {
        @include grid-column(6);
    }
}

.form-input {
    font-size: $fontSize-root - 4;
    border-radius: 50px;

    @include smin-xs {
        font-size: $fontSize-root - 2;
        border-radius: 100px;
    }

    @include smin-m {
        font-size: $fontSize-root;
    }
}

.form-prefixPostfix {
    &.wrap {
        flex-wrap: wrap;
    }

    .form-input,
    .button {
        display: block;
        width: 100%;
    }

    .button {
        height: auto;
        min-width: 142px;
    }

    .form-input {
        @include smin-m {
            width: auto;
        }

        @include placeholder {
            color: $formInput-placeholder-color;
        }
    }

    .button {
        width: auto;
        margin: 0;
    }

    .form-inlineMessage {
        margin-bottom: 16px;

        @include smin-m {
            margin-bottom: 0;
        }
    }
}

.form-prefixPostfix--centeredColumn {
    &.wrap {
        flex-direction: column;
    }

    .g-recaptcha {
        margin: 2em auto 0;
    }

    .form-prefixPostfix-button--postfix {

        @include smin-m {
            margin: 1em 0 spacing("half");
        }
    }

}

.form-prefixPostfix-button--postfix {
    margin: spacing("half") 0 0;
    order: 1; // 1

    @include smin-m {
        margin: 0 0 0 spacing("half");
        order: 0; // 1
    }
}


// Increment field
// -----------------------------------------------------------------------------

.form-increment {
    position: relative;
    max-width: 176px;
    min-height: 60px;

    .button {
        color: $header-font-color;
        background-color: transparent;
        margin: 0;
        padding: 0;
        border: none;
        width: 42px;
        height: 100%;
        position: absolute;

        &:before,
        &:after {
            content: "";
            position: absolute;
            top: 50%;
            transform: translateY(-50%);
            display: block;
            line-height: 22px;
            background-color: $color-black;
        }
    }

    [data-action="dec"] {
        bottom: 0;
        left: 0;

        &:before {
            left: 16px;
            width: 14px;
            height: 1px;
        }

        @include smin-m {
            &:before {
                left: 24px;
            }
        }
    }

    [data-action="inc"] {
        bottom: 0;
        right: 0;

        &:before {
            right: 16px;
            width: 14px;
            height: 1px;
        }

        &:after {
            right: 22px;
            width: 1px;
            height: 14px;
        }

        @include smin-m {
            &:before {
                right: 24px;
            }

            &:after {
                right: 30px;
            }
        }
    }
}

.form-input--incrementTotal {
    font-size: $fontSize-root;
    font-weight: 600;
    text-align: center;
    width: 100%;
    min-height: 60px;
    color: $color-black;
    border: 1px solid #cbcbcb;
    border-radius: 50px;
    padding: 0 30px;
    max-width: 100%;
    display: inline-block;
    vertical-align: middle;

    .form-field--success & {
        float: none;
    }

    @include smin-xs {
        border-radius: 100px;
    }
}


// Stencil form options
// -----------------------------------------------------------------------------
//
// 1. Issue with FastClick JS library workaround: https://github.com/ftlabs/fastclick/issues/351
// 2. Resetting font due to inline-block whitespace issue.
// 3. Added for STENCIL-3384. Did not change directly on .form-option-variant to avoid 1.
// 4. Added for STRF-4031. Breaks 3 for small screens.
//

.form-option {
    background-color: $color-white;
    border: 1px solid stencilColor("input-border-color");
    color: #757575;
    cursor: pointer;
    display: inline-block;
    font-size: 0; // 2
    margin-bottom: spacing("third");
    margin-right: spacing("third");
    padding: 1px;
    transition: $anchor-transition;

    &:hover {
        border-color: stencilColor("input-border-color-active");
    }

    .form-radio:checked + & {
        border-color: stencilColor("checkRadio-color");
        color: stencilColor("checkRadio-color");
        font-weight: $fontWeight-semibold;
    }
}

.form-option-swatch {
    .form-radio:checked + & {
        border-color: transparent;
        background-color: transparent;
        box-shadow: none;

        .form-option-variant {
            &:before {
                content: "";
                position: absolute;
                top: 0;
                left: 0;
                width: 40px;
                height: 40px;
                z-index: 2;
                border-radius: 50%;
                border: 2px solid $color-white;
                box-shadow: 0px 0px 0px 1px $color-black;
                transition: $anchor-transition;
            }
        }
    }
}

.form-option-variant {
    display: inline-block;
    font-size: $fontSize-root - 4;
    min-width: auto;
    padding: 1px 16px;
    pointer-events: none;
    text-align: center;

    @include smin-s {
        font-size: $fontSize-root - 2;
    }

    @include smin-m {
        font-size: $fontSize-root;
    }
}

.form-option-variant--color,
.form-option-variant--pattern {
    @include square(22);
    min-width: initial;
    padding: 0;

    @include smin-xs { // 4
        pointer-events: inherit; // 3
    }
}

.form-option-variant--pattern {
    background-position: center;
    background-repeat: no-repeat;
}


// Form input (small)
// -----------------------------------------------------------------------------
.form-input--small {
    font-size: $input-small-fontSize;
    height: $input-small-height;
    line-height: 1rem;
    padding: $input-small-padding;
}


// Form min/max input row
// -----------------------------------------------------------------------------
.form-minMaxRow {
    @include clearfix();
    margin-left: -$form-minMaxRow-column-gutter;
    margin-right: -$form-minMaxRow-column-gutter;

    .form-field {
        @include grid-column(4);
        margin-bottom: 0;
        padding-left: $form-minMaxRow-column-gutter;
        padding-right: $form-minMaxRow-column-gutter;
    }

    .button {
        margin-bottom: 0;
        padding-left: 0;
        padding-right: 0;
        width: 100%;
        min-height: 57px;
        line-height: 55px;
        border-radius: 57px;
    }

    .form-input {
        font-size: $fontSize-root + 2;
        padding: 0 10px;
        min-height: 57px;
        max-width: 100px;
        border-radius: 12px;
        border-color: #2a2a2a;
    }
}


// Form row modifications
// -----------------------------------------------------------------------------
//
// 1. If there are more than 2 fields per form-row--half, clear the left of every
// odd one.
//
// -----------------------------------------------------------------------------

.form-row--half > .form-field:nth-child(odd) {
    @include smin-xs {
        clear: left;
    }
}

// Out of stock product attributes
[data-product-attribute] {
    .form-option {
        overflow: hidden;
        position: relative;

        &.unavailable {
            background-color: $productOptions-disable-background-color;
            opacity: $productOptions-disable-opacity;

            &::before {
                background-color: $productOptions-strikethrough-color;
                content: "";
                height: 2px;
                left: -5px;
                position: absolute;
                top: 11px;
                transform: rotate(-45deg);
                width: 141%;
            }
        }
    }
}

[data-product-attribute="set-radio"],
[data-product-attribute="product-list"] {
    .unavailable {
        opacity: $productOptions-disable-opacity * 2;
        text-decoration: line-through;
    }
}

// Custom
.form-row .form-column .form-field {
    padding-left: 0;
    padding-right: 0;
}

textarea,
textarea.form-input {
    padding: 20px;
    border-radius: 40px;
}

components/citadel/actionBar/actionBar.s:
// =============================================================================
// ACTION BAR (CSS)
// =============================================================================

.actionBar {
    display: inline-block;
    margin: 0;

    .form-field {
        margin: 0;
    }

    @include smin-m {
        +.actionBar {
            margin-left: 5px;
        }
    }
}

.actionBar-wrapper {
    padding-bottom: 30px;
}

.actionBar-section {
    .form-label {
        font-size: $actionBar-form-label-fontSize - 2;
    }

    .form-input,
    .form-select {
        border: 0;
        padding-left: $actionBar-form-input-padding;
        width: auto;
    }

    .form-label,
    .form-input,
    .form-select {
        display: inline-block;

        @media (min-width: $screen-m) and (max-width: ($screen-xl - 1px)) {
            display: block;
            padding: 0 30px 0 0;
            margin: 0;
        }
    }

    .form-select {
        color: $color-black;
        height: $input-small-height - remCalc($input-border-width * 2);
        position: relative;
        bottom: -1px;
    }
}

.form-select-custom {
    background-color: transparent;
}

[data-sort-by] {
    .filter-dropdown {
        min-width: 200px;
    }

    @include smin-xxl {
        .filter-dropdown {
            min-width: 262px;
        }
    }
}

.filter-dropdown {
    min-width: 200px;
    vertical-align: top;
    display: inline-block;
    list-style: none;
    margin: 0;
    border: 1px solid #a9a9a9;

    .icon {
        position: absolute;
        right: 20px;
        padding: 3px;
    }

    .filter-action {
        padding: 11px 20px;
    }

    .dropdown-label {
        color: #a9a9a9;
    }

    .dropdown-menu {
        background-color: stencilColor("navUser-dropdown-backgroundColor");
        border: 1px solid stencilColor("navUser-dropdown-borderColor");
        box-shadow: container("dropShadow");
        max-width: 100%;
        z-index: 20;

        &.is-open {
            left: 0 !important;
            top: 100% !important;
        }
    }

    a {
        color: $color-black;
    }

    @include smin-xxl {
        min-width: 250px;
    }
}

.actionBar-filter {
    @include smax-xxxl {
        .form-select {
            bottom: 0;
            min-height: 46px;
            background-position: right 0.5rem top 0.8rem;
        }

        .filter-dropdown {
            .icon {
                width: 16px;
                height: 24px;
                padding: 0;
            }

            .filter-action {
                padding: 8px 16px;

                .dropdown-label {
                    color: $color-black;
                    font-size: $fontSize-root;
                    font-weight: $fontWeight-semibold;
                }
            }
        }
    }
}

components/citadel/loading/loading.scss:
// =============================================================================
// LOADING (CSS)
// =============================================================================
//
// 1. If set to false, there is no outputted CSS for this component.
//
// -----------------------------------------------------------------------------

.loadingOverlay {
    background-color: rgba(stencilColor("loadingOverlay-backgroundColor"), 0.4);
    display: none;
    z-index: zIndex("low");
}

.animated-loading {
    animation-duration: 2.2s;
    animation-fill-mode: forwards;
    animation-iteration-count: infinite;
    animation-name: shimmer;
    animation-timing-function: linear;
    background: $color-custom-loading;
    background: linear-gradient(to right, $color-custom-loading 4%, #CDE1FF 25%, $color-custom-loading 36%);
    background-size: 1200px 100%;

    * {
        opacity: 0;
    }
}

@keyframes shimmer {
    0% {
        background-position: -1200px 0;
    }

    100% {
        background-position: 1200px 0;
    }
}

components/stencil/ribbon/ribbon.scss:
// =============================================================================
// RIBBON (CSS)
// =============================================================================
//
// 1. A ribbon needs a container set to relative in order to position itself.
// 2. Top right text badge. Shows "On sale" as an example.
//
// -----------------------------------------------------------------------------

.has-ribbon { // 1
    position: relative;
}

.ribbon { // 2
    background-color: $ribbon-backgroundColor;
    color: $ribbon-color;
    cursor: $ribbon-cursor;
    font-weight: $ribbon-fontWeight;
    margin: $ribbon-margin;
    padding: $ribbon-padding;
    position: absolute;
    right: $ribbon-positionRight;
    text-transform: $ribbon-textTransform;
    top: $ribbon-positionTop;
    z-index: 1;
}

components/stencil/navPages/navPages.scs:
// Page Navigation
// -----------------------------------------------------------------------------
//
// Purpose: Contains the styles for the page navigation, deals with 2 viewsdue to
// responsive layout of that navigation
//
// 1. It's not exactly a grid row, as it's a little further down the DOM tree, but
// I use it here to grab the max width centering.
// 2. Prevent document scrolling by clipping its body
// 3. Prevent inertia scrolling of document
//
// -----------------------------------------------------------------------------


//
// Shared styles for sub page navigation
// -----------------------------------------------------------------------------

%navPage-childList {
    border-left: 1px solid $navPage-childList-borderColor;
    margin: spacing("quarter") spacing("half");
    padding-left: spacing("half");
}

//
// When navPages is open
// -----------------------------------------------------------------------------

.has-activeNavPages {
    overflow: hidden; // 2
    -webkit-overflow-scrolling: auto; // 3
}

//
// Top level Pages navigation container
// -----------------------------------------------------------------------------

.navPages-container {
    bottom: 0;
    display: none;
    height: 100%;
    left: 0;
    -webkit-overflow-scrolling: touch;
    position: absolute;
    right: 0;
    z-index: zIndex("lower");

    @include smin-m {
        background: transparent;
        display: block;
        height: auto;
        padding: 0;
        position: static;
    }

    &.is-open {
        display: block;
        padding-top: $header-height;
    }
}

//
// Top level Pages navigation list styles
// -----------------------------------------------------------------------------

.navPages {
    background-color: $navPages-backgroundColor;
    height: 100%;

    @include smin-m {
        @include nav($style: "navBar", $wrapper: "nav");
        background: none;
        font-size: 0; // 1
        overflow-y: visible;
        padding: 0;

        .navPages-item {
            position: static;
        }
    }

    ul {
        list-style: none;
    }

    .navPage-subMenu-list {
        @extend %navPage-childList;
        border-left: 0;
        list-style: none;
        margin: 0;
        padding-left: 0;
    }

    .navPage-subMenu-item {
        padding: 0 5px;
    }
}

.navPages-list {
    margin: 0;

    +.navPages-list {
        padding-top: 50px;
    }

    @include smax-m {
        .has-subMenu:not(.no-subMenu) {
            span {
                pointer-events: none;
            }
        }
    }

    @include smin-m {
        display: inline-block;
        float: none;
    }
}

.navPages-action-depth-max.has-subMenu.is-root.is-open {
    .navPages-action-moreIcon {
        @include smin-m {
            transform: rotate(0);
        }
    }
}

.navPages-action-depth-max.has-subMenu.is-open {
    border-bottom: 1px solid $navPages-list--user-borderColor;
    text-align: center;

    @include smin-m {
        border-bottom: 0;
        text-align: left;
    }
}
.navPages-item {
    @include smin-m {
        &:focus-within {
            >.navPage-subMenu {
                display: block;
            }
        }
    }
}

.navPages-action {
    display: block;
    font-size: $fontSize-root;
    font-weight: $fontWeight-semibold;
    padding: spacing("half") 0;
    text-decoration: none;
    text-transform: unset;

    @include smin-m {
        color: $navPage-Color;
        display: inline-block;
        padding: 15px 30px 15px 0;

        &:hover,
        &.activePage {
            color: $navPage-Color-hover;
            -webkit-text-stroke: .5px $navPage-Color-hover;

            svg {
                fill: $navPage-Color-hover;
                stroke: $navPage-Color-hover;
            }
        }
    }

    @include smin-xxl {
        padding: 20px 40px 20px 0;
    }

    @include smin-hd {
        .left & {
            padding: 10px 50px 10px 0;
        }
    }

    svg {
        fill: $color-black;
        width: 24px;
        height: 24px;
        transition: $anchor-transition;

        @include smin-m {
            fill: $header-color;
            stroke: $header-color;
            width: 10px;
            height: 10px;
        }

        @include smin-hd {
            width: 18px;
        }
    }
}

.navPages-action--storeCredit {
    color: $header-font-color;
}

.navPages-action--compare {
    display: none;

    &.show {
        display: block;
    }
}

.navPages-action-moreIcon {
    @include square(8px);
    margin-left: spacing("half");
    transform: rotate(-90deg);

    @include smin-m {
        transform: none;
    }

    .navPages-list & {
        margin: 0 0 0 8px;
    }
}


// Sub page navigation and children of sub pages
// -----------------------------------------------------------------------------

.navPage-childList {
    @extend %navPage-childList;
    display: none;

    @include smin-m {
        border: 0;
        display: block;
    }

    &.is-open {
        display: block;
    }

    .navPage-subMenu-action:not(.navPages-action-depth-max)+& {
        margin-left: spacing("double"); //

        @include smin-m {
            margin-left: 0;
        }
    }
}


// Sub menu, first level down from top level categories
// -----------------------------------------------------------------------------
//
// 1. navBar mixin has lots of decendant child selectors to deal with
//
// -----------------------------------------------------------------------------

.navPage-subMenu {
    display: none;
    outline: 0;

    @include smin-m {
        background-color: $color-custom-white;
        padding: 15px 0 20px;
        position: absolute;
        text-align: left;
        left: 0;
        width: 100%;

        .has-megamenu & {
            padding: 60px 0 0;
        }

        &.is-open {
            display: block;
        }

        .navPages-action-moreIcon {
            display: none;
        }
    }

    .navPage-subMenu-item-child {
        position: relative;

        .navPage-subMenu {
            left: 215px;
            top: 0;
            animation: halo-fadeIn .6s cubic-bezier(0, 0, .3, 1) forwards;
        }
    }
}

.has-subMenu.is-root+.navPage-subMenu-horizontal {
    @include smin-m {
        border-left: 0;
        height: $navPage-subMenu-horizontal-height;
        left: inherit;
        margin: auto;
        top: inherit;
        width: $navPage-subMenu-horizontal-width;
    }
}

.navPage-subMenu-horizontal {
    .navPages-action-moreIcon {
        @include smin-m {
            display: inline-block;
            transform: rotate(-90deg);
            position: relative;
            top: 5px;
        }
    }
}

.navPage-subMenu-item {
    padding: spacing("half") 0;

    @include smin-m {
        padding: 0 0.75rem;

        .navPages-action {
            text-transform: none;
            width: 100%;
        }
    }
}

.navPage-subMenu-item-child,
.navPage-subMenu-item-parent {
    width: 100%;

    @include smin-m {
        .navPage-subMenu-action {
            padding: 0 15px;
            margin-bottom: 12px;
            width: 100%;
            text-transform: unset;
            line-height: 1.4;

            &.has-subMenu {
                padding: 0 15px;
                margin-bottom: 16px;
            }
        }
    }
}

.navPage-subMenu-action {
    .collapsible-icon-wrapper {
        box-sizing: content-box;
        display: inline-block;
        height: 100%;
        text-align: right;
        width: 100%;

        @include smin-m {
            display: none;
        }
    }
}


//
// Child pages of sub pages navigation lists
// -----------------------------------------------------------------------------
.navPage-childList-action {
    font-weight: fontWeight("normal");
    padding: 0 0 spacing("quarter");
}


//
// Quick search inside the mobile nav
// -----------------------------------------------------------------------------

.navPages-quickSearch {
    border-bottom: container("border");
    margin-bottom: spacing("half");

    @include smin-m {
        display: none;
    }

    >.container {
        padding: 0;
    }

    .form {
        margin: 0;
    }

    .form-field {
        margin-bottom: 0;
    }
}

//
// NavPages user section for mobile nav panel
// -----------------------------------------------------------------------------

.navPages-list--user {
    @include smin-m {
        display: none;
    }
}

//
// Mega Menu
// -----------------------------------------------------------------------------

.has-megamenu {
    @include smin-m {
        .has-dropdown {
            margin-bottom: 48px;
        }

        &.is-open {
            >.navPages-action {
                -webkit-text-stroke: .5px $navPage-Color-hover;
            }
        }
        >.navPage-subMenu {
            &.is-open {
                .container {
                    display: flex;
                }
            }

            .container {
                padding: 0;

                >.navPage-subMenu-list {
                    column-count: 3;
                    width: calc(100% - 400px);
                }
            }

            .navPage-subMenu {
                display: block;
                padding: 0;
                position: unset;
                animation: none;
            }
        }

        &.no-image {
            >.navPage-subMenu {
                >.navPage-subMenu-list {
                    width: 100%;
                }
            }
        }

        &.has-image {
            .imageArea {
                width: 400px;
                padding: 0 15px;
                margin-bottom: 60px;
            }

            .image {
                width: 100%;
            }
        }
    }
}

//
// Mobile Menu
// -----------------------------------------------------------------------------

.halo-menu-sidebar {

    .container {
        padding: 0;
    }

    .navPages {
        display: block;
        font-size: 0;
        letter-spacing: 0;
        @include clearfix;
    }

    .navPages-container {
        display: block;
        height: 100%;
        overflow-y: auto;
        overflow-x: hidden;
        padding: 0;
    }

    .navPages-list {
        position: relative;
        transition-duration: 0.4s;
        transform: none;
        transition-property: transform;

        .has-activeNavPages & {
            display: block;
        }
    }

    .navPages-item {
        width: 100%;
        margin: 0;
        border-bottom: 1px solid $color-custom-greyDark;
        position: relative;

        .navPages-action {
            color: $color-textBase;
            margin: 0;
            padding: 0 15px;
            display: flex;
            cursor: pointer;
            align-items: center;
            text-transform: capitalize;
            background-color: transparent;
            justify-content: space-between;
            line-height: 45px;
            font-weight: $fontWeight-medium;

            .text {
                display: flex;
                align-items: center;
                padding: 0;
                color: inherit;
                position: relative;
                background: transparent !important;
            }

            .navPages-action-moreIcon {
                width: auto;
                height: 45px;
                z-index: 10;
                top: auto;
                display: flex;
                align-items: center;
                justify-content: center;
                order: 0;
                text-align: right;
                font-size: 0;
                pointer-events: none;
                transform: none;
                margin-left: 0;

                svg {
                    fill: $color-textBase;
                    color: $color-textBase;
                    stroke: $color-textBase;
                    pointer-events: none;
                    display: inline-block;
                    vertical-align: middle;
                    transition: $anchor-transition;
                    transform: rotate(-90deg);
                    width: 13px;
                    height: 13px;
                }
            }

            &:not(.navPages-action-end) {
                .text {
                    pointer-events: none;
                }
            }
        }

        >.navPages-action {
            font-weight: $fontWeight-medium;
            line-height: 45px;
            text-transform: uppercase;

            .text {
                white-space: nowrap;
            }
        }

        .navPage-subMenu {
            top: 0;
            right: 100%;
            left: auto;
            width: 100%;
            height: 100%;
            opacity: 1;
            pointer-events: auto;
            visibility: visible;
            transform: none;
            box-shadow: none;
        }

        >.navPage-subMenu {
            >.navPage-subMenu-list {
                >.navPage-subMenu-item-child {
                    >.navPages-action {
                        font-weight: $fontWeight-medium;
                    }
                }
            }
        }

        .navPage-subMenu-item-child {
            padding-right: 0;
            border-bottom: 1px solid $color-custom-greyDark;

            &.navPage-subMenu-all {
                display: inline-block;
            }

            .navPages-action {
                font-size: $fontSize-root;
            }

            &:last-child {
                border-bottom: none;
            }
        }

        .navPage-subMenu-title {
            >.navPages-action {
                font-weight: $fontWeight-medium;
                background-color: #f5f5f5;

                .navPages-action-moreIcon {
                    display: flex;
                    align-items: center;
                    justify-content: flex-start;

                    svg {
                        transform: unset;
                        width: 25px;
                        height: 25px;
                        margin: 0 0 0 -8px;
                    }
                }

                .text {
                    max-width: calc(100% - 70px);
                    white-space: nowrap;
                    overflow: hidden;
                    text-overflow: ellipsis;
                    display: inline-block;
                    vertical-align: top;
                    width: 100%;
                    margin: 0 auto;
                    text-align: center;
                    text-transform: uppercase
                }
            }
        }

        +.navPages-item {
            margin-left: 0;
        }

        &:last-child {
            border-bottom: none;
        }
    }

    .navPage-subMenu-action-all {
        border-bottom: 1px solid $color-custom-greyDark;
    }

    .imageArea {
        width: 100%;
    }

    .image {
        width: 100%;
        padding: 5px 10px;
    }

    .socialLinks {
        margin: 10px 0 0 0 !important;

        .icon svg {
            fill: $color-textBase;
        }
    }

    .socialLinks-item {
        display: inline-block;
        vertical-align: top;
    }

    .is-open {
        border-bottom: 0;

        >.navPages-action {
            display: none !important;
        }

        >.navPage-subMenu {
            display: block;
            position: relative;
            top: 0;
            right: 0;
            margin: 0;

            >.navPage-subMenu-list {
                padding-left: 0;

                >.navPage-subMenu-item-child {
                    &.navPage-subMenu-title {
                        display: block;
                    }
                }
            }
        }
    }

    .is-hidden {
        display: none !important;
    }

    .cur-flag {
        margin-right: 10px;

        img {
            @include square(16);
            border-radius: 50%;
        }
    }

    @media(min-width: 1025px) {
        .halo-sidebar-header {
            display: none;
        }
    }

    @media(max-width: 1024px) {
        .halo-sidebar-header {
            .close {
                right: 5px;
            }
        }
    }
}

.navPages-action-wishlist,
.navPages-action-phone {
    svg {
        width: 16px;
        height: 16px;
        position: relative;
        top: -1px;
        margin-right: 10px;
    }
}

.navPages-action-currency {
    svg {
        margin-left: -4px;
        margin-right: 6px;
        position: relative;
        top: -1px;
    }
}

components/stencil/navUser/navUser.scss:
// =============================================================================
// USER NAVIGATION (CSS)
// =============================================================================
//
// 1. Corrects the spacing added by .navUser-or
// 2. Can't use top: 50% because its container `.header` changes its height to
//    100% when mobile menu is expanded
// 3. Make the triangle for store credit dropdown centered
// 4. Needs to be 100% so its dropdown can take full width in mobile viewport
// 5. Needs to be lower than logo zIndex, otherwise, logo is not clickable
// 6. Make the triangle for currency dropdown right aligned
// 7. Corrects mini cart positioned outside viewport. Since this resets right
//    position, nudge dropdown away from the side of viewport in mobile viewport.
// 8. This corrects mini cart dropdown arrow alignment in mobile viewport by
//    setting the previous styles to medium breakpoint and adjusts for nudge in (7).
//
// -----------------------------------------------------------------------------

.navUser {
    @include nav($style: "navBar", $wrapper: "nav");
    @include verticalPositionMiddle("absolute");
    right: 0;
    top: 26px; // 2
    width: 100%; // 4
    z-index: zIndex("lowest"); // 5.

    @include breakpoint("medium") {
        padding: 0;
        position: relative;
        right: auto;
        top: auto;
        transform: none;
        z-index: zIndex("high");
    }

    .dropdown-menu {
        background-color: stencilColor("navUser-dropdown-backgroundColor");
        border: 1px solid stencilColor("navUser-dropdown-borderColor");
        box-shadow: container("dropShadow");
        position: absolute;
        padding: 10px;

        &.is-open {
            &::before {
                @include css-triangle(
                    $triangle-direction: "bottom",
                    $triangle-size: 10px,
                    $triangle-color: stencilColor("navUser-dropdown-borderColor")
                );
                bottom: 100%;
                left: spacing("half");
                position: absolute;
            }

            &::after {
                @include css-triangle(
                    $triangle-direction: "bottom",
                    $triangle-size: 8px,
                    $triangle-color: stencilColor("navUser-dropdown-backgroundColor")
                );
                bottom: 100%;
                left: spacing("half") + remCalc(2px);
                position: absolute;
            }
        }
    }
}

.navUser-section {
    float: none;
    @include breakpoint("medium") {
        position: relative; // 7
    }
}

.navUser-action {
    color: $header-color;
    font-weight: fontWeight("semibold");
    text-decoration: none;

    &:hover,
    &.is-open {
        color: $header-color;

        svg {
            fill: $header-color;
        }
    }

    svg {
        fill: $header-color;
        transition: $anchor-transition;
    }
}

.navUser-action--currencySelector + .dropdown-menu {
    &::before {
        left: auto !important; // 6
        right: spacing("half"); // 6
    }

    &::after {
        left: auto !important; // 6
        right: spacing("half") + remCalc(2px); // 6
    }
}

.navUser-action--storeCredit + .dropdown-menu {
    max-width: remCalc(300px);
    padding: spacing("single");

    &::before {
        left: remCalc(140px) !important; // 3
    }

    &::after {
        left: remCalc(142px) !important; // 3
    }
}

.navUser-action-divider {
    border-right: container("border");
    color: $header-font-color;
    display: inline-block;
    padding: 0 remCalc(20) 0 0;
    text-decoration: none;
}

.navUser-item {
    a {
        color: $header-color;

        &:hover {
            color: $header-color;
        }
    }

    .item-label {
        margin: 0 4px;
    }

    @include breakpoint("medium") {
        &.navUser-item--social {
            margin-top: rem-calc(5px);
            padding-right: rem-calc(5px);
        }

        &.navUser-item--divider {
            font-size: rem-calc(25px);
            margin-top: rem-calc(8px);
            padding-left: rem-calc(2px);
        }
    }

    @include smax-m {
        float: none;
    }
}

.navUser-item--alt .navUser-action {
    padding: 9px 28px 9px 0;
}

.navUser-item--alt:last-child .navUser-action {
    padding: 9px;
    background-color: $buttonStyle-primary-backgroundColor;

    @include smin-xxxl {
        padding: 9px 0;
        min-width: 129px;
    }
}

.navUser-item--cart {
    display: block;

    .navUser-action {
        color: $header-color;

        &:hover,
        &.is-open {
            color: $header-color;
        }
    }

    .dropdown-menu {
        max-width: remCalc(320px);

        &.is-open {
            left: auto !important; // 7
            right: remCalc(5px); // 7
            top: auto !important; // 7
            @include breakpoint("medium") {
                right: 0; // 7
            }

            &::before,
            &::after {
                left: auto;
            }

            &::before {
                right: spacing("half") - remCalc(5px); // 8
                @include breakpoint("medium") {
                    right: spacing("half"); // 8
                }
            }

            &::after {
                right: spacing("half") - remCalc(3px); // 8
                @include breakpoint("medium") {
                    right: spacing("half") + remCalc(2px); // 8
                }
            }
        }
    }
}

.navUser-item-cartLabel {
    display: none;

    @include breakpoint("small") {
        display: inline;
    }
}

.navUser-item--compare {
    display: none;

    &.show {
        display: block;
    }
}

.navUser-item--account {
    .navUser-or {
        + .navUser-action {
            margin-right: -0.5rem; // 1
        }
    }

    .navUser-action {
        display: inline-block;
    }
}

.navUser-or {
    color: $header-color;
    cursor: default;
    display: inline-block;
    margin: 0 remCalc(-12px);
    padding: 0 2px;
    position: relative;
    z-index: 1;
}

// Quick search
// -----------------------------------------------------------------------------
//
// 1. Overriding the inline styles from Foundation as this is a unique dropdown.
//
// -----------------------------------------------------------------------------

.navUser-action--quickSearch {
    &.is-open {
        position: relative;

        &::before {
            @include css-triangle(
                $triangle-direction: "bottom",
                $triangle-size: 10px,
                $triangle-color: color("greys", "lightest")
            );
            bottom: 0;
            left: 50%;
            position: absolute;
            transform: translateX(-50%);
        }
    }
}

.dropdown--quickSearch {
    background-color: $dropdown--quickSearch-backgroundColor;
    display: none;
    padding: spacing("single") 0;

    @include breakpoint("small") {
        padding: spacing("double");
    }

    &.is-open {
        display: block;
        left: 0 !important; // 1
        outline: none;
        right: 0 !important; // 1
        width: 100% !important;
        z-index: zIndex("high");
    }

    .form {
        margin: auto;
        max-width: 30rem;
    }

    .form,
    .form-field {
        margin-bottom: 0;
    }

    .form-input {
        font-size: fontSize("small");
        height: unset;
    }

    .productGrid {
        padding: spacing("single") 0 0;

        @include breakpoint("small") {
            padding: spacing("double") 0 0;
        }
    }
}

// Counter Pill
// -----------------------------------------------------------------------------
//
// 1. Hardcoded intentionally for precise pixels.
//
// -----------------------------------------------------------------------------

.countPill {
    position: absolute;
    top: -3px;
    right: -4px;
    font-size: $fontSize-root - 4;
    @include square(18);
    color: $color-black;
    background-color: $buttonStyle-tertiary-backgroundColor;
    border-radius: 50%;

    @include smin-m {
        position: relative;
        top: -1px;
        right: auto;
        font-size: $fontSize-root - 2;
        @include square(22);
        background-color: $color-white;
    }
}

