components/stencil/heroCarousel/heroCaro:
// HERO CAROUSEL
// -----------------------------------------------------------------------------
//
// Purpose: Styles the hero carousel component, which basically adds extras to the
// vendor slick carousel, to specifically display hero images.
//
// 1. Use object-fit on the image tag to keep aspect ratio and cover the space
//    created by the outer div. The JS will detect browsers that do not support
//    object-fit and instead copy the image src to a the background-image of the
//    wrapper div and add the compat-object-fit class.
//
// 3. Allows image to scale on large screens while preventing the top and bottom
//    from becoming cut off.
// -----------------------------------------------------------------------------

.heroCarousel-content--empty {
    background-color: transparent;
}

.heroCarousel-image {
    @include smax-m {
        height: 100%;
        object-fit: cover;
        object-position: 50% 50%;
        width: 100%;
    }
}

.heroCarousel-image-wrapper {
    @include smax-m {
        display: flex;
        align-items: flex-start;
        justify-content: center;
        height: 100vw;
        width: 100%;
        max-height: remCalc(550px);
        transition: height 0.3s ease;

        .image {
            width: 100%;
            height: 100%;
        }
    }

    @include smax-xs {
        max-height: remCalc(450px);
        min-height: remCalc(450px);
    }
}

.heroCarousel-content {
    position: absolute;
    background-color: stencilColor('carousel-bgColor');
    top: 50%;
    left: 0;
    right: 0;
    z-index: 3;
    width: 100%;
    max-width: 320px;
    text-align: center;
    transform: translateY(-50%);
    white-space: normal;
    margin: 0;

    &.heroCarousel-content--empty {
        background-color: transparent;
    }

    &.heroCarousel-content--left {
        left: 10px;
        text-align: left;
        transform: translateY(-50%);
    }

    &.heroCarousel-content--center {
        left: 50%;
        transform: translate(-50%, -50%);
    }

    &.heroCarousel-content--right {
        left: auto;
        right: 10px;
        text-align: right;
        transform: translateY(-50%);
    }

    .heroCarousel-title {
        @include smax-xs {
            font-size: $h4-font-size + 2;
        }
    }

    @include smin-s {
        max-width: 480px;

        &.heroCarousel-content--left {
            left: 50px;
        }

        &.heroCarousel-content--right {
            right: 50px;
        }
    }

    @include smin-xxl {
        max-width: 630px;

        &.heroCarousel-content--left {
            left: calc((100% - 1600px)/2);
        }

        &.heroCarousel-content--right {
            right: calc((100% - 1600px)/2);
        }
    }
}

.heroCarousel-title {
    display: inline-block;
    vertical-align: top;
    width: 100%;
    font-weight: $fontWeight-bold;
    color: $carousel-title-color;
    margin: 0 0 15px 0;
}

.heroCarousel-description {
    display: inline-block;
    vertical-align: top;
    margin-bottom: 15px;
    max-width: 680px;
    color: $carousel-description-color;
}

components/stencil/productCarousel/produ:
// PRODUCT CAROUSEL
// -----------------------------------------------------------------------------
//
// Purpose: Styles the product carousel component, which basically adds extras to the
// vendor slick carousel, to specifically display product
//
// 1. If there's no JS layout the products out based on the grid.
//
// -----------------------------------------------------------------------------

.productCarousel {
    min-width: 100%;
}

.productCarousel-slide {
    .card {
        margin-bottom: 0;

        .card-title {
            overflow-wrap: break-word;
        }
    }
}

[data-per-view-des="4"],
[data-per-view-des="3"],
[data-per-view-des="2"] {
    &:not(.swiper-initialized) {
        display: block;
        white-space: nowrap;
        overflow: auto;
        font-size: 0;
        margin: 0 -5px;

        .swiper-slide {
            display: inline-block;
            vertical-align: top;
            overflow: hidden;
            padding: 0 5px;
        }

        .content-wrapper {
            white-space: normal;
        }

        @include smax-xs {
            &[data-swiper-disable-mb="true"] {
                white-space: normal;
            }
        }

        @include smin-s{
            margin: 0 -8px;

            .swiper-slide {
                padding: 0 8px;
            }
        }

        @include smin-m {
            margin: 0 -15px;

            .swiper-slide {
                padding: 0 15px;
            }
        }
    }
}

[data-per-view-des="5"] {
    &:not(.swiper-initialized) {
        .swiper-slide {
            width: 20%;

            &:nth-child(n+6) {
                display: none;
            }
        }
    }
}

[data-per-view-des="4"] {
    &:not(.swiper-initialized) {
        .swiper-slide {
            width: 25%;

            &:nth-child(n+5) {
                display: none;
            }
        }
    }
}

[data-per-view-des="3"] {
    &:not(.swiper-initialized) {
        .swiper-slide {
            width: 33.3334%;

            &:nth-child(n+4) {
                display: none;
            }

            @include smax-xs {
                width: 100%;
            }
        }
    }
}

[data-per-view-des="2"] {
    &:not(.swiper-initialized) {
        .swiper-slide {
            width: 50%;

            &:nth-child(n+3) {
                display: none;
            }
        }
    }
}

@include smax-m {
    [data-per-view-tab="3"] {
        &:not(.swiper-initialized) {
            .swiper-slide {
                width: 33.3334%;

                &:nth-child(n+4) {
                    display: none;
                }
            }
        }
    }

    [data-per-view-tab="2"] {
        &:not(.swiper-initialized) {
            .swiper-slide {
                width: 50%;

                &:nth-child(n+3) {
                    display: none;
                }
            }
        }
    }
}

@include smax-xs {
    [data-per-view-mob="2"] {
        &:not(.swiper-initialized) {
            .swiper-slide {
                width: 50%;

                &:nth-child(n+3) {
                    display: none;
                }
            }
        }
    }
}

