components/stencil/toggleLink/toggleLink:
// =============================================================================
// TOGGLE LINK (CSS)
// =============================================================================


// Toggle link
// -----------------------------------------------------------------------------

$toggle-spacing: spacing("single") + spacing("half");

.toggle {
    border-top: container("border");
    margin-bottom: $toggle-spacing;

    &:last-of-type {
        margin-bottom: $toggle-spacing * 2;
    }
}

.toggle-title {
    margin-bottom: $toggle-spacing;
}

.toggleLink {
    color: $header-font-color;

    .toggle-title & {
        float: right;
        line-height: 24px;
    }
}

.toggleLink-text {
    font-family: $fontFamily-sans;
    font-size: $fontSize-root - 2;
    font-weight: fontWeight("normal");
    line-height: 1.556;
    text-decoration: underline;

    @include smin-s {
        font-size: $fontSize-root;
    }

    @include smin-m {
        font-size: $fontSize-root + 2;
    }
}

.toggleLink-text--off {
    display: inline-block;

    .toggleLink.is-open & {
        display: none;
    }
}

.toggleLink-text--on {
    display: none;

    .toggleLink.is-open & {
        display: inline-block;
    }
}

.toggle-content {
    display: none;

    &.is-open {
        display: block;
    }
}

components/stencil/banners/banners.scss:
// =============================================================================
// Banners Component
// =============================================================================


// Banners
// -----------------------------------------------------------------------------
//
// Purpose: Control the look and feel of the top and bottom site banners
//
// -----------------------------------------------------------------------------

.banners {
    background-color: stencilColor("color-primary");
    color: stencilColor("color-white");
    padding: spacing("half") 0;
    text-align: center;

    p:last-child {
        margin-bottom: 0;
    }

    a {
        color: color("whites", "bright");
    }
}

.banner {
    @include grid-row;

    + .banner {
        margin-top: spacing("single");
    }
}

components/stencil/cart/cart.scss:
// =============================================================================
// CART (CSS)
// =============================================================================
//
// 1. Override server-generated inline-css
//
// -----------------------------------------------------------------------------

// Cart variables
// -----------------------------------------------------------------------------

$cart-item-spacing:                     spacing("single");
$cart-content-padding-right:            4px;

$cart-thumbnail-maxWidth:               remCalc(100px);
$cart-thumbnail-height:                 remCalc(100px);
$cart-thumbnail-maxHeight:              14rem;
$cart-thumbnail-paddingVertical:        0.5rem;

$cart-item-label-offset:                $cart-thumbnail-maxWidth + $cart-item-spacing;
$card-preview-zoom-bottom-offset:       6rem;

//
// Shared styles for additional checkout buttons
// -----------------------------------------------------------------------------

%additionalCheckoutButtons {
    @include clearfix;

    .FloatRight {
        @include clearfix;

        p {
            float: none !important;
            margin: spacing("third") 0;
            text-align: right;
        }

        div {
            float: right;
        }
    }
}

// Cart layout
// -----------------------------------------------------------------------------
//
// 1. On mobile and tablet we need to re-structure the table to appear more like
//    a list, then visually show it as a table on larger screens only.
//
// 2. Hide the table headings on mobile and tablet, show them for larger screens.
//
// -----------------------------------------------------------------------------

.cart {
    display: block; // 1
    margin-bottom: spacing("single");
    width: 100%;

    @include smin-m {
        display: table; // 1
    }
}

.cart-header {
    display: none; // 2

    &-item {
        padding: 0 0 24px;
    }

    @include smin-m {
        display: table-header-group; // 2
        text-align: left;
    }
}

.cart-list {
    display: block; // 1

    .cart-item-spacer {
        height: 10px;
    }

    @include smin-m {
        display: table-row-group; // 1
    }
}

.cart-item {
    @include clearfix;
    border-top: 1px solid #cccccc;
    clear: both;
    display: block; // 1
    padding: 20px 0;

    &:last-child {
        border-bottom: 1px solid #cccccc;
    }

    @include smin-m {
        display: table-row; // 1
        padding: 0;

        &::before,
        &::after {
            display: none;
        }
    }
}

.cart-item-block {
    display: block; // 1

    @include smin-m {
        display: table-cell; // 1
        padding: 24px 0 24px;

        &.cart-item-title {
            padding-right: 20px;
        }
    }

    .definitionList {
        margin-bottom: spacing("quarter");
    }
}

.cart-item-figure {
    @include cart-item-lazy-load-img-placeholder;
    float: left;
    margin-bottom: 0;
    position: relative;
    text-align: center;
    width: get-width(stencilString("productthumb_size"));

    @include smin-m {
        float: none;
        padding: 24px 0;
    }

    @include smax-xs {
        width: 100px;
    }
}

.cart-item-fixed-image {
    width: 100%;
}

.cart-item-image {
    max-height: $cart-thumbnail-maxHeight;
    max-width: get-width(stencilString("productthumb_size"));

    @include smin-m {
        margin-left: 0;
    }

    @include smax-xs {
        width: 100px;
    }
}

.cart-item-title {
    display: block;
    height: auto;
    overflow: hidden;
    padding-left: 10px;

    @include smin-m {
        display: table-cell;
        width: 510px;
    }
}

.cart-item-info {
    clear: none;
    float: left;
    overflow: hidden;
    width: auto;
    font-size: $fontSize-root + 2;
    font-weight: $fontWeight-medium;

    + .cart-item-info {
        margin-top: 0;
    }

    @include smin-m {
        float: none;
        width: 220px;

        &.cart-item-total {
            width: 140px;
        }
    }

    @include smax-m {
        min-height: 68px;
        display: flex;
        align-items: center;
        padding: 10px;
    }

    @include smax-xs {
        width: grid-calc(4, $total-columns);
        justify-content: center;
    }
}

// Cart content
// -----------------------------------------------------------------------------
.cart-header-quantity,
.cart-item-quantity {
    text-align: center;
}

.cart-item-quantity {
    .form-increment {
        display: inline-block;
        margin: 0;
        vertical-align: middle;
        min-height: 44px;
    }

    .form-input--incrementTotal {
        min-height: 44px;
        max-width: 124px;
        background-color: transparent;
    }
}

.cart-item-label {
    display: none;
}

.cart-item-value {
    padding-left: 0;
}

.cart-item-brand {
    color: $header-font-color;
    margin-bottom: spacing("eighth");
}

.cart-item-name {
    display: inline-block;
    font-size: $fontSize-root + 2;
    margin: 0;

    &__label {
        display: inline-block;
    }

    + .definitionList {
        margin-top: spacing("quarter");
    }
}

.cart-item-options--giftWrapping {
    .icon {
        display: inline-block;
        width: 16px;
        height: 16px;
        fill: $color-textBase;
        margin: 0 7px 0 0;
        position: relative;
    }
}

.cart-remove {
    background-color: container("fill", "dark");
    border-radius: 50%;
    cursor: pointer;
    float: right;
    margin-top: 0;
    padding: 3px;
    transition: background-color 0.15s ease;

    &.cart-remove--spacer {
        visibility: hidden;
    }

    > svg {
        fill: stencilColor("icon-color");
        width: 100%;
        height: 100%
    }

    @include smin-xs {
        margin-left: 16px;
    }

    @include smax-m {
        @include square(30);
        position: absolute;
        top: 15px;
        left: -5px;
        margin: 0;
    }
}


// Cart totals
// -----------------------------------------------------------------------------

[data-cart-totals] {
    @include smin-m {
        float: right;
        width: 553px;
    }
}

.cart-totals {
    list-style: none;
    margin: 0;

    .cart-total {
        display: block;
        padding: 0 0 1px 0;
        @include clearfix;

        &-label {
            font-size: $fontSize-root;
            font-weight: $fontWeight-medium;
            color: $color-textBase;
            line-height: 25px;
            text-align: left;
            padding: 11px 0;
            float: left;

            strong {
                font-weight: $fontWeight-medium;
            }

            a {
                font-weight: $fontWeight-normal;
                transition: unset;

                .text {
                    padding: 0 0 1px 0;
                    background-image: linear-gradient(transparent 97%, $color-textLink 3%);
                    background-repeat: repeat-x;
                    background-position-y: 0;
                }
            }
        }

        &-value {
            font-size: $fontSize-root;
            font-weight: $fontWeight-semibold;
            color: $color-textBase;
            line-height: 25px;
            text-align: right;
            padding: 11px 0;
            float: right;

            p[translate] {
                font-size: $fontSize-root;
                font-weight: $fontWeight-normal;
                color: $header-font-color;
                margin: 0;
            }

            &.cart-total-grandTotal {
                font-size: $fontSize-root + 2px;
                font-weight: $fontWeight-bold;
                margin-top: 3px;
            }
        }

        .form-label {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            color: $color-textBase;
            margin: 0;
            line-height: 50px;
        }

        .form-inlineMessage {
            text-align: left;
        }

        +.cart-total {
            border-top: 1px solid #cccccc;
        }

        &.cart-total-grandtotal {
            .cart-total-value {
                font-size: $fontSize-root + 4px;
                font-weight: $fontWeight-bold;
                line-height: 1.2;
            }
        }
    }

    @media (min-width: 1025px) {
        .cart-total {
            &-label {
                a {
                    &:hover {
                        color: $color-textLink-hover;

                        .text {
                            background-image: linear-gradient(transparent 97%, $color-textLink-hover 3%);
                        }
                    }
                }
            }
        }
    }
}

.cart-total {
    @include clearfix;
    border-bottom: 0;

    @include smin-m {
        + .cart-total {
            border-top: container("border");
        }
    }

    .form-input {
        display: inline-block;
        padding: spacing("third");
        vertical-align: top;
        width: grid-calc(9, $total-columns);
    }

    .form-select {
        background-position: right remCalc(5px) center;
        background-size: remCalc(20px) remCalc(20px);
        height: remCalc(32px);
        margin-bottom: spacing("half");
        padding: 0 spacing("half");
    }

    .form-inlineMessage {
        text-align: left;
    }
}

.cart-total-label {
    color: $header-font-color;
    width: grid-calc(6, $total-columns);

    @include smin-m {
        display: block;
        padding-left: 0;
        text-align: left;
    }
}

.cart-total-value {
    text-align: right;

    @include smin-m {
        width: grid-calc(6, $total-columns);
    }
}

.cart-total-label,
.cart-total-value {
    float: left;
    padding: spacing("third") 0 spacing("third") $cart-item-spacing;

    @include smin-m {
        padding: spacing("base") 0;
    }
}

.cart-total-grandTotal {
    font-size: fontSize("small");
    padding-bottom: spacing("eighth");
    padding-top: spacing("eighth");
    position: relative;

    @include smin-m {
        padding: spacing("base") 0;
        top: -(spacing("sixth"));
    }
}


// Cart actions
// -----------------------------------------------------------------------------

.coupon-code-add,
.coupon-code-cancel,
.shipping-estimate-show,
.gift-certificate-add,
.gift-certificate-cancel {
    color: stencilColor("color-textLink");
    padding: 0;
    text-decoration: underline;
    transition: color 0.15s ease;
    font-weight: $fontWeight-medium;
    font-size: $fontSize-root;

    &:hover {
        color: $header-font-color;
    }
}

.coupon-code-cancel,
.shipping-estimate-show[aria-expanded="true"] {
    font-style: italic;
}

.coupon-form,
.cart-gift-certificate-form {
    @include clearfix;
    font-size: 0;
    margin-bottom: 0;

    .form-input,
    .button {
        float: left;
    }

    .form-input {
        width: 73%;
    }

    .button {
        margin: 0 0 20px 2%;
        padding-left: spacing("half");
        padding-right: spacing("half");
        width: 25%;
    }
}

.estimator-form {
    margin-bottom: 0;

    .form-input {
        margin-bottom: spacing("half");
        width: 100%;
    }

    .button--primary {
        width: 60%;
        float: right;
    }
}

.estimator-form-label {
    clear: both;
    float: left;
    font-weight: fontWeight("normal");
    font-size: $fontSize-root;
    margin-bottom: 0;

    .form-label {
        line-height: remCalc(32px);
        margin-bottom: 0;
    }
}

.estimator-form-label-text {
    margin-left: remCalc(10px);
}

.estimator-form-input {
    float: right;
    margin-bottom: 1em;
    width: 60%;

    .form-input,
    .form-select {
        margin-bottom: 0;
    }
}

.estimator-form-input--price {
    width: 30%;
}

.estimator-form-row {
    @include clearfix;
}

.estimator-form-toggleUPSRate {
    display: block;
    margin-bottom: 1em;
}

.cart-actions {
    @include clearfix;
    clear: both;

    .button {
        display: inline-block;
        float: right;
        margin-bottom: 0;
    }

    .checkoutMultiple {
        clear: right;
        display: block;
        float: right;
        padding-top: spacing("quarter");
    }
}

.cart-additionalCheckoutButtons {
    @extend %additionalCheckoutButtons;
    width: 100%;

    @include smin-xl {
        * {
            min-width: 100%;
        }

        .or-use-label {
            text-align: center !important;
        }
    }

    @include smax-xl {
        max-width: 370px;

        .or-use-label {
            text-align: center !important;
        }
    }
}

// Cart Preview
// -----------------------------------------------------------------------------
//
// 1. Need to vertically align a block of text
//
// -----------------------------------------------------------------------------

.previewCartWrapper {
    max-height: calc(100vh - #{$card-preview-zoom-bottom-offset});
    overflow: auto;
}

.previewCartList {
    @include u-listReset;
    border-collapse: collapse;
    display: table;
    table-layout: fixed;
    width: 100%;
}

.previewCartItem {
    @include grid-row;
    display: table-row;
    line-height: lineHeight("smaller");
}

.previewCartItem-image,
.previewCartItem-content {
    display: table-cell; // 1
    vertical-align: middle;
}

.previewCartItem-image {
    @include lazy-loaded-padding("productthumb_size");
    padding: 0;
    position: relative;
    text-align: center;

    img {
        @include lazy-loaded-img;
    }

    &::after {
        @include breakpoint("xxsmall") {
            padding-bottom: 75%;
        }

        @include breakpoint("xsmall") {
            padding-bottom: 100%;
        }
    }
}

.previewCartItem-content {
    @include grid-column(8, $float: false);
    padding-left: 0;
}

.previewCartItem-name {
    margin: 0;
    text-transform: none;

    > a {
        text-decoration: none;
    }
}

.previewCartItem-brand,
.previewCartItem-name {
    margin-bottom: spacing("eighth");
}

.previewCartItem-brand,
.previewCartItem-price {
    display: inline-block;
    font-size: $fontSize-root;
    font-weight: $fontWeight-semibold;
    color: $header-font-color;
}

.previewCartAction {
    @include grid-row;
    border-top: container("border");
    display: block;

    @include breakpoint("xxsmall") {
        padding: spacing("half") spacing("quarter");
    }

    @include breakpoint("xsmall") {
        padding: spacing("single") spacing("half");
    }

    .estimator-form-label {
        margin-bottom: 30px;
    }

    .button {
        margin: 0;
        padding-left: spacing("half");
        padding-right: spacing("half");
        width: 100%;
    }
}

.previewCartAction-checkout,
.previewCartAction-viewCart {
    @include grid-column(6);

    &.previewCartAction-viewCart--sole {
        @include grid-column($columns: 6, $center: true);
    }
}

.previewCartAction-checkout {
    padding-right: spacing("quarter");
}

.previewCartAction-checkoutMultiple {
    clear: both;
    padding-left: spacing("half");
    padding-top: spacing("quarter");
}

.previewCartAction-viewCart {
    padding-left: spacing("quarter");
}

.previewCart-emptyBody {
    padding: spacing("double");
    text-align: center;
}

.previewCart-additionalCheckoutButtons {
    @extend %additionalCheckoutButtons;
    padding-bottom: spacing("single");
    padding-right: spacing("single");

    .or-use-label {
        text-align: center !important;
    }

    * {
        min-width: 100%;
    }
}

.progress-percent {
    font-size: 10px;
    color: $color-white;
    display: block;
    height: 16px;
    line-height: 14px;
    letter-spacing: 0.02rem;
}

.page-cart.has-calculate-shipping {

    [data-cart-status] .alertBox {

        &.Upsell.Banner,
        &.Congratulations.Banner {
            display: none;
        }
    }

    .page-header {
        +.halo-free-shipping-message {
            margin-bottom: 25px;
            height: 46px;
        }
    }

    .shipping-message {
        .progress-percent {
            display: none;
        }
    }

    .halo-free-shipping-message {
        padding-top: 0;
        padding-bottom: 0;
        margin-bottom: 24px;

        .progress-shipping {
            height: 14px;
            margin: 0 0 12px 0;
        }

        .shipping-message-100 {
            padding-top: 11px;
            padding-bottom: 11px;
            background: #fafafa;
        }
    }
}

.cart-content-wrapper {
    display: block;
    letter-spacing: 0;
    @include clearfix;

    .cart-content-item {
        display: block;
    }
}

.cart-item-edit {
    display: inline-block;
    vertical-align: top;
    margin: 5px 0 0 0;

    .icon {
        display: inline-block;
        vertical-align: -2px;
        width: 16px;
        height: 16px;
        fill: $color-textBase;
        margin: 0 2px 0 0;
    }


    @media (min-width: 1025px) {
        &:hover {
            .text {
                text-decoration: underline;
            }
        }
    }
}

components/stencil/facetLabel/facetLabel:
// =============================================================================
// FACET LABEL (CSS)
// =============================================================================

.facetLabel {
    background: $facetLabel-backgroundColor;
    border-radius: $facetLabel-borderRadius;
    padding: 9px 38px 9px 12px;
    text-decoration: none;

    .icon {
        @include square(18px);
        display: inline-block;
        fill: $color-white;
        top: 50%;
        right: 10px;
        transform: translateY(-50%);
    }
}

components/stencil/addressBox/addressBox:
// =============================================================================
// ADDRESS BOX (CSS)
// =============================================================================

// Address layout
// -----------------------------------------------------------------------------
//
// 1. Using display: inline-block; to vertically align blocks.
//    Font-size gets redefined under the .address block.
//
// -----------------------------------------------------------------------------

.addressList {
    font-size: 0; // 1
    list-style: none;
    margin-left: -(spacing("half"));
    margin-right: -(spacing("half"));
}

.address {
    display: inline-block;
    padding: 0 spacing("half");
    vertical-align: top;
    width: 100%;

    @include breakpoint("small") {
        width: grid-calc(6, $total-columns);
    }

    @include breakpoint("large") {
        width: grid-calc(4, $total-columns);
    }
}


// Address
// -----------------------------------------------------------------------------
//
// 1. Redefine the font-size as we're removing white-space on the parent by
//    reducing the size to 0.
// 2. Adding a min-height to improve the consistency of the address blocks.
// 3. On larger screens we position: absolute; the .form-actions so they're
//    consistently in line. We need to adjust padding to accommodate this.
//
// -----------------------------------------------------------------------------

.panel--address {
    font-size: fontSize("base"); // 1
    position: relative;

    .panel-body {
        min-height: remCalc(250px); // 2
        padding: spacing("single");

        @include breakpoint("small") {
            padding: spacing("single") spacing("single") (spacing("double") + spacing("double")); // 3
        }
    }

    .form-actions {
        margin-bottom: -(spacing("half"));
        margin-top: spacing("single");
        text-align: left;

        @include breakpoint("small") { // 3
            bottom: spacing("single");
            position: absolute;
            width: auto;
        }

        .button {
            margin-bottom: spacing("half");

            + .button {
                margin-left: 0;
                margin-top: 0;

                @include breakpoint("small") {
                    margin-left: spacing("quarter");
                }
            }
        }
    }
}

.address-title {
    margin: 0 0 spacing("half");
    text-transform: none;
}

.address-details {
    list-style: none;
    margin: 0 0 spacing("half");
}

.address-details--postal {
    font-size: $fontSize-root;
}

.address-label,
.address-description {
    display: inline-block;
    font-size: fontSize("base");
    margin: 0;
}

.address-label {
    color: $header-font-color;
    font-weight: fontWeight("normal");
}

.panel--newAddress {
    display: block;
    text-align: center;
    text-decoration: none;

    &:hover {
        .address-title {
            color: $color-textLink-hover;
        }
    }

    .panel-body {
        background-color: transparent;
        border: container("border");
        display: block;
    }

    .address-title {
        margin-bottom: 0;
        transition: $anchor-transition;
    }
}

.address-addNew {
    left: 50%;
    position: absolute;
    top: 50%;
    transform: translateX(-50%) translateY(-55%);
}

.address-symbol {
    font-size: fontSize("hero");
    line-height: 1;
}

components/stencil/videoGallery/videoGal:
// =============================================================================
// VIDEO GALLERY
// =============================================================================

.videoGallery-main {
    @include flex-video-container();
    margin-bottom: $videoGallery-spacing;
    padding-top: 0; // 1
}

.videoGallery-list {
    @include u-listBullets("none");
    @include grid-row($behavior: "nest");
    font-size: 0;
}

.videoGallery-item {
    @include grid-column(12);
    display: inline-block;
    float: none;
    vertical-align: top;

    @include breakpoint("medium") {
        width: grid-calc(6, $total-columns);
    }
}

.video {
    @include media;
    display: block;
    margin-bottom: $videoGallery-spacing;
    opacity: 0.4;
    text-decoration: none;
    transition: opacity 100ms ease-out;

    @include breakpoint("medium") {
        margin-bottom: $videoGallery-spacing;
    }

    &:hover,
    &.is-active {
        color: color("greys", "darker");
        opacity: 1;
    }
}

.video-figure {
    margin-right: $videoGallery-spacing;
}

.video-title {
    margin-bottom: 0;
    margin-top: 0;
    text-transform: none;
}

.video-description {
    font-size: fontSize("base");
    margin-bottom: 0;
}

.halo_videoGallery {
    .videoGallery-main {
        margin-bottom: 15px;
    }

    .videoGallery-list {
        text-align: center;
        overflow-x: auto;
        overflow-y: hidden;
        white-space: nowrap;
        margin: 0;
        padding: 0 0 10px 0;

        .videoGallery-item {
            white-space: normal;
            width: auto;
            max-width: 180px;
            padding: 0 15px 0 0;

            &:last-child {
                padding-right: 0;
            }
        }

        .video {
            display: block;
            margin-bottom: 0;

            .video-figure,
            .video-body {
                float: none;
                display: block;
            }

            .video-figure {
                margin-right: 0;
            }

            .video-body {
                display: none;
            }
        }
    }
}

components/stencil/previewCart/previewCa:
// =============================================================================
// PREVIEW CART (CSS)
// =============================================================================
//
// 1. Override server-generated inline-css
//
// -----------------------------------------------------------------------------

.halo-free-shipping-message {
    .progress-shipping {
        width: 100%;
        height: 16px;
        margin: 0 0 12px 0;
        border-radius: 20px;
        background-color: #eee;

        .progress-meter {
            position: relative;
            display: block;
            height: 100%;
            text-align: center;
            border-radius: 20px;
            background: linear-gradient(90deg, #284AC8 28.91%, #9DA8CF 90.35%);
            transition: .9s linear;
            transition-property: width, background-color;
        }
    }

    .shipping-message {
        font-size: $fontSize-root - 2;
        color: $color-textSecondary;
        line-height: 19px;
        margin: 0;
        display: flex;
        align-items: center;
        justify-content: space-between;

        span {
            +span {
                font-style: normal;
            }
        }
    }

    .shipping-message-100 {
        display: block;
        text-align: center;
        padding: 0;
        margin: 0;
        color: $header-font-color;
        font-size: $fontSize-root;
        line-height: 1.75;
        background: transparent;
    }
}

.previewCartHeader {
    padding: 0 0 14px;
}

.previewCart {
    margin: 0;

    .previewCartList {
        list-style: none;
        padding: 0;
        margin: 40px 0 20px;
        display: block;
        width: 100%;
        overflow: auto;
        max-height: calc(100vh - 400px);
    }

    .previewCartItem {
        display: block;
        max-width: 100%;
        font-size: 0;
        padding: 0 0 20px;
        margin: 0 0 20px;
        border-bottom: 1px solid #eee;
    }

    .previewCartItem-image {
        display: inline-block;
        vertical-align: top;
        width: 90px;
        padding: 0;
        position: relative;
        text-align: center;

        @include smin-xs {
            width: 122px;
        }
    }

    .previewCartItem-content {
        display: inline-block;
        vertical-align: top;
        width: calc(100% - 90px);
        padding: 0 0 0 10px;
        text-align: left;

        @include smin-xs {
            padding: 0 0 0 22px;
            width: calc(100% - 122px);
        }
    }

    .previewCartItem-name {
        overflow-wrap: break-word;
        position: relative;
    }

    .previewCartItem-options {
        position: relative;
        padding: 0 30px 0 0;
        width: auto;
    }

    .previewCartItem-variant {
        display: block;
        width: 100%;
        margin: 0 0 10px 0;

        span {
            +span {
                &:before {
                    content: ',';
                    margin: 0 4px 0 -3px;
                }
            }
        }
    }

    .previewCartItem-edit {
        display: inline-block;
        vertical-align: top;
        position: absolute;
        top: -2px;
        right: 0;

        .icon {
            width: 18px;
            height: 18px;
            fill: #888888;
        }

        @include smin-m {
            &:hover {
                .icon {
                    fill: $color-textBase;
                }
            }
        }
    }

    .previewCartItem-price {
        display: block;
        width: 100%;
    }

    .previewCartItem-qty {
        width: 100%;
        max-width: 130px;
        padding: 0;
        position: relative;

        .form-label {
            margin-bottom: 10px;
        }

        .form-input--incrementTotal {
            font-weight: $fontWeight-normal;
        }
    }

    .form-increment {
        max-width: 112px;
        min-height: 40px;

        .form-input {
            height: 40px;
            min-height: 40px;
        }

        .button {
            min-height: 40px;
            line-height: 38px;
        }
    }

    .form-input--incrementTotal {
        font-size: $fontSize-root - 2;
    }

    .previewCartItem-remove {
        position: absolute;
        margin: 0;
        top: auto;
        transform: unset;
        bottom: 18px;
        left: auto;
        right: 0;
        display: block;
        height: auto;
        width: auto;
        transition: unset;
        padding: 0;
        @include clearfix;

        @media (max-width: 375px) {
            position: relative;
            bottom: 0;
            padding-top: 10px;
        }
    }

    .previewCartTotalsPrice {
        list-style: none;
        margin: 0;
        padding: 0 0 40px;
    }

    .previewCartTotals {
        display: block;
        font-size: 0;
        padding: 0;
        @include clearfix;

        .previewCartTotals-label {
            display: inline-block;
            vertical-align: top;
            width: 120px;
            text-align: left;
            text-transform: none;
        }

        .previewCartTotals-value {
            display: inline-block;
            vertical-align: top;
            width: calc(100% - 120px);
            text-align: right;
        }

        &.grandTotal {
            .previewCartTotals-value {
                padding: 0;
                top: -1px;
            }
        }
    }

    .previewCartAction {
        border: 0;
        display: block;
        font-size: 0;
        letter-spacing: 0;
        padding: 3px 0 0 0;
        margin: 0;
        width: auto;

        .previewCartAction-shipping {
            margin-bottom: 40px;
        }

        .previewCartAction-checkout,
        .previewCartAction-viewCart {
            display: inline-block;
            vertical-align: top;
            width: 50%;
            padding: 0;

            .button {
                min-height: 36px;
                line-height: 34px;

                @include smin-s {
                    min-height: 50px;
                    line-height: 48px;
                }

                @include smin-m {
                    min-height: 57px;
                    line-height: 55px;
                }
            }
        }

        .previewCartAction-viewCart {
            padding-right: 3px;
        }

        .previewCartAction-checkout {
            padding-left: 3px;
        }

        .button {
            margin: 0;
            padding: 0;
            width: 100%;

            &.shipping-estimate-submit {
                margin-bottom: 25px;
            }
        }
    }

    .productView-info {
        > :first-child {
            margin-top: 0;
        }
    }

    .productView-info {
        margin-top: 5px;
        margin-bottom: 5px;
        width: 100%;

        .productView-info-name {
            min-width: 65px;
            font-weight: 700;
            letter-spacing: 0;
            text-align: left;
            width: auto;
        }

        .productView-info-value {
            margin-bottom: 2px;
            padding-left: 0;
        }

        .productView-info-name,
        .productView-info-value {
            float: left;
            font-family: $body-font-family;
            font-size: 14px;
            text-transform: capitalize;
        }
    }

    .productView-brand {
        text-transform: capitalize;
        margin-bottom: 9px;
    }

    .productView-price {
        font-size: 15px;

        @media (min-width: 1024px) {
            font-size: 20px;
        }
    }

    &.previewCart2 {
        padding: 0;
        display: block;
        font-size: 0;
        letter-spacing: 0;

        .halo-free-shipping-message {
            padding-top: 0;
            padding-bottom: 30px;
        }

        .previewCartView {
            .productView-brand {
                margin-bottom: 0;
            }

            .productView-title {
                font-size: $fontSize-root;
            }
        }
    }

    @media (min-width: 1025px) {
        .previewCartItem-remove {
            &:hover {
                svg {
                    fill: stencilColor('color-textBase');
                }

                span {
                    color: #ce2b1e;
                    background-image: linear-gradient(transparent 97%, #ce2b1e 3%);
                    background-repeat: repeat-x;
                    background-position-y: 0;
                }
            }
        }
    }
}

.previewCart-emptyBody {
    display: block;
    font-size: $fontSize-root;
    color: $header-font-color;
    margin: 0;
    text-align: center;
    padding: 35px 0 5px;
    border: 1px solid transparent;
}

.previewCartView {
    display: block;
    width: 100%;
    text-align: center;

    .productView-image {
        display: inline-block;
        vertical-align: top;
        width: 100px;
    }

    .productView-details {
        display: inline-block;
        vertical-align: top;
        width: calc(100% - 100px);
        padding: 0 0 0 15px;
        text-align: left;
        float: none;
    }

    .productView-brand {
        margin: 0 0 2px 0;
    }

    .productView-title {
        margin: 0;
    }

    .productView-price {
        margin: 0 0 5px 0;
    }

    .productView-info {
        display: flex;
        justify-content: center;
        align-items: center;
        margin: 0;

        +.productView-info {
            margin-top: 4px;
        }
    }

    .productView-info-name,
    .productView-info-value {
        margin: 0;
    }

    .productView-info-value {
        margin: 0 0 0 5px;
    }

    @media (min-width: 551px) {
        display: inline-block;
        vertical-align: middle;
        width: 50%;
        padding: 0 15px 0 0;

        .productView-info {
            justify-content: flex-start;
        }
    }

    @media (min-width: 768px) {
        width: calc(100% - 280px);
        padding-right: 50px;

        .productView-image {
            width: 170px;
        }

        .productView-details {
            width: calc(100% - 170px);
            padding-left: 30px;
        }
    }

    @media (min-width: 992px) {
        padding-right: 20px;
    }
}

.previewCartCheckout {
    display: block;
    width: 100%;
    text-align: center;
    margin: 15px 0 0 0;

    .button {
        display: block;
        margin: 0;
        width: 100%;

        +.button {
            margin-top: 20px;
        }
    }

    .previewCartCheckout-additionalCheckoutButtons {
        p {
            font-size: $fontSize-root;
            font-weight: $fontWeight-normal;
            text-transform: uppercase;
            color: $header-font-color;
            margin: 0 0 8px 0;
        }

        .CheckoutButton {
            margin-bottom: spacing("base");

            &:first-child {
                margin-top: 13px;
            }

            &:last-child {
                margin-bottom: spacing("single");
            }
        }
    }

    .previewCartCheckout-subtotal {
        display: block;
        margin: 37px 0 0 0;
        border: 1px solid #c5c5cf;
        border-radius: 0;

        .previewCartCheckout-price {
            display: block;
            text-align: center;
            position: relative;
            top: -10px;
            margin: 0 0 7px 0;
        }

        span {
            display: inline-block;
            vertical-align: top;
            position: relative;
            top: -15px;
            padding: 0 20px;
            color: $header-font-color;
        }

        p {
            margin: 0;
        }
    }

    [data-cart-quantity] {
        color: $header-font-color;
        margin: 15px 0 20px 0;
    }

    &.previewCartCheckout2 {
        width: 100%;
        text-align: center;

        .button {
            width: auto;

            +.button {
                margin-top: 15px;
            }
        }
    }

    @media (min-width: 768px) {
        display: inline-block;
        vertical-align: top;
        width: 100%;
        padding: 0 0 0 15px;
    }
}

.suggestiveCart {
    display: none;
    text-align: center;

    @media (min-width: 1025px) {
        display: block;
    }
}

// Quick Edit Cart
// -----------------------------------------------------------------------------

.product-edit-wrapper {
    display: block;

    .product-edit-item {
        display: block;
        padding: 35px 0;
        margin: 0 40px;

        .product-edit-itemLeft {
            display: flex;
            flex-wrap: wrap;
            align-items: flex-start;
            padding: 0;
            width: 100%;
        }

        .product-edit-itemRight {
            display: block;
            width: 100%;
            padding: 0;
            margin: 30px 0 0 0;
        }

        +.product-edit-item {
            border-top: 1px solid $color-custom-greyDarker;
        }

        &:first-child {
            padding-top: 32px;
        }
    }

    .product-edit-image {
        display: block;
        width: 100px;
        margin: 0 15px 0 0;
        position: relative;

        img {
            width: 100%;
        }

        .remove {
            position: absolute;
            top: -16px;
            left: -16px;
            border-radius: 50%;
            background: #000;
            width: 32px;
            height: 32px;
            padding: 5px;

            .icon {
                display: block;
                width: 100%;
                height: 100%;
                fill: $color-white;
            }
        }
    }

    .product-edit-content {
        display: block;
        width: calc(100% - 115px);
        margin-bottom: -5px;

        .product-edit-title {
            display: block;
        }

        .product-edit-variant {
            padding: 0;

            span {
                display: inline;

                +span {
                    margin-left: 4px;

                    &:before {
                        content: '/';
                        margin: 0 4px 0 0;
                    }
                }
            }
        }

        .product-edit-price {
            display: block;
            margin: 9px 0 0 0;
            position: relative;

            .price-section {
                &.rrp-price--withoutTax {
                    margin: 0 0 5px 0;
                }

                &.non-sale-price--withoutTax {
                    display: inline-block;
                    vertical-align: bottom;
                    margin: 0 13px 0 0;

                    +.price-section--withoutTax {
                        display: inline-block;
                        vertical-align: bottom;
                    }
                }

                &.price-none {
                    margin: 0;

                    +.price-section--withoutTax {
                        margin-left: 13px;
                    }

                    &[style="display: none;"] {
                        +.price-section--withoutTax {
                            margin-left: 0;
                        }
                    }
                }
            }

            .price--non-sale {
                font-weight: $fontWeight-semibold;
            }

            >p {
                color: $header-font-color;
                margin-bottom: 0;
            }

            .price-section--saving {
                color: $header-font-color;
                margin: 5px 0 0 0;
            }
        }

        .product-edit-increment {
            display: inline-block;
            vertical-align: top;
            width: 100%;
            max-width: 130px;
            margin: 15px 0 0 0;
            padding: 0;
            position: relative;
            font-size: 0;
            letter-spacing: 0;
        }
    }

    .product-edit-remove {
        display: block;
        width: 100%;
        margin: 15px 0 0 0;
        text-align: center;

        .button {
            min-width: 150px;
        }
    }

    .form {
        margin: 0;
        padding: 0;
    }

    .form-field {
        margin: 0;
        padding: 0;
    }

    .product-edit-optionsStock {
        color: #ea625e;
        margin: 7px 0 0 0;
    }

    .product-edit-message {
        margin-top: 15px;
        margin-bottom: 0;
    }

    .product-edit-itemFirst {
        .product-edit-title {
            span {
                display: inline-block;
                vertical-align: middle;
                background-image: linear-gradient(transparent 97%, $color-textLink-hover 3%);
                background-repeat: repeat-x;
                background-position-y: -1px;

                &+span {
                    font-size: $fontSize-root;
                }
            }
        }
    }

    .form-increment {
        max-width: 112px;
        min-height: 40px;

        .form-input {
            height: 40px;
            min-height: 40px;
        }

        .button {
            min-height: 40px;
            line-height: 38px;
        }
    }

    @media (min-width: 551px) {
        .product-edit-content {
            .product-edit-increment {
                max-width: 193px;

                .form-label {
                    width: 75px;
                    margin-bottom: 0
                }
            }
        }
    }

    @media (min-width: 768px) {
        .product-edit-item {
            display: flex;
            align-items: flex-start;

            .product-edit-itemLeft {
                padding-right: 15px;
                width: 50%;
            }

            .product-edit-itemRight {
                width: 50%;
                margin-top: 0;
                padding-left: 15px;
            }
        }
    }

    @media (min-width: 1025px) {
        .product-edit-image {
            .link {
                &:hover {
                    background-color: #666;

                    .icon {
                        fill: $color-white;
                    }
                }
            }
        }
    }
}

.product-edit-action {
    position: absolute;
    top: auto;
    bottom: 0;
    left: 0;
    width: 100%;
    background: $color-white;
    z-index: 20;
    padding: 0 40px 40px 40px;

    .product-add-more {
        padding: 12px 0;

        .add-more {
            position: relative;
            margin: 0 0 0 20px;
            padding: 0;
            transition: unset;

            &:before {
                content: "";
                position: absolute;
                top: 9px;
                width: 12px;
                height: 2px;
                right: calc(100% + 9px);
                background-color: $color-textLink;
            }

            &:after {
                content: "";
                position: absolute;
                top: 4px;
                width: 2px;
                height: 12px;
                right: calc(100% + 14px);
                background-color: $color-textLink;
            }
        }
    }

    .product-edit-button {
        max-width: 200px;
        margin: 0 auto;

        .button {
            min-height: 57px;
            line-height: 55px;
            width: 100%;
            text-transform: uppercase;
            margin-bottom: 0;

            &:before {
                content: '';
                display: none;
                width: 20px;
                height: 20px;
                border: 2px solid $color-white;
                border-radius: 100%;
                border-top: 2px solid transparent;
                -webkit-animation: load-animate infinite linear 1s;
                animation: load-animate infinite linear 1s;

                @include smin-m {
                    border-color: $header-font-color;
                }
            }

            &.loading {
                pointer-events: none;
                font-size: 0;

                &:before {
                    display: inline-block;
                    vertical-align: middle;
                }
            }

            &[disabled] {
                pointer-events: none;
            }
        }
    }
}

components/stencil/rating/rating.scss:
// =============================================================================
// RATING (CSS)
// =============================================================================

.icon--ratingEmpty {
    svg {
        fill: stencilColor("icon-ratingEmpty");
    }
}

.icon--ratingFull {
    svg {
        fill: stencilColor("icon-ratingFull");
    }
}

[data-test-info-type="productRating"] {
    min-height: 19px;
    font-size: 0;

    svg {
        width: 16px;
        height: 16px;
    }
}


// Small rating
// -----------------------------------------------------------------------------
//
// 1. Vertically aligns stars to container. Icons are off by default.
//    Icons are all vertically aligned to middle, however middle is not middle,
//    and therefore need to be adjusted to actually sit at middle.
//
// -----------------------------------------------------------------------------

.rating--small {
    display: inline-block;

    .icon {
        @include square(14px);
        margin-top: -3px; // 1
    }
}

components/stencil/applePay/applePay.scs:
.apple-pay-checkout-button {
    background-color: $applePay-black;
    background-image: -webkit-named-image(apple-pay-logo-white);
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: 100% 60%;
    border-radius: $applePay-borderRadius;
    cursor: pointer;
    display: none;
    max-height: remCalc(64px);
    min-height: remCalc(32px);
    min-width: 90px;
