components/products/bulk-discount-rates:
{{#if bulk_discount_rates.length}}
    {{#if product}}<dt class="productView-info-name">{{lang 'products.bulk_pricing.title'}}</dt>{{/if}}
    <dd class="productView-info-value">
        <a href="{{product.url}}#bulk_pricing"
           {{#unless is_ajax }}data-reveal-id="modal-bulk-pricing"{{/unless}}>
            {{lang 'products.bulk_pricing.view'}}
        </a>
    </dd>
    <div id="modal-bulk-pricing" class="modal modal--small" data-reveal>
        <div class="modal-header">
            <h2 class="modal-header-title">{{lang 'products.bulk_pricing.modal_title'}}</h2>
            {{> components/common/modal/modal-close-btn}}
        </div>
        <div class="modal-body">
            <p>{{lang 'products.bulk_pricing.instructions'}}</p>
            <ul>
                {{#each bulk_discount_rates}}
                <li>
                    {{lang 'products.bulk_pricing.range' min=min max=max}}
                    {{#if type '===' 'percent'}}
                    {{lang 'products.bulk_pricing.percent' discount=discount.formatted}}
                    {{else if type '===' 'fixed'}}
                    {{lang 'products.bulk_pricing.fixed' discount=discount.formatted}}
                    {{else if type '===' 'price'}}
                    {{lang 'products.bulk_pricing.price' discount=discount.formatted}}
                    {{/if}}
                </li>
                {{/each}}
            </ul>
        </div>
    </div>
{{/if}}

components/products/price:
{{!-- There are 2 code paths for generating the PDP pricing HTML determined by the following #and condition.
If a 'price_range' exists then we generate all HTML in price-range.html, otherwise it is defined here. Both code
paths generate the same HTML structure with some differences in whether that element is displayed by default (css styling).

If you are making a change here or in price-range.html, you probably want to make that change in both files. --}}

{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
    {{> components/products/price-range price=price price-range-custom=price-range-custom}}
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax rrp-price--withTax{{#unless price.non_sale_price_with_tax}} price-none{{/unless}}" {{#unless price.rrp_with_tax}}style="display: none;"{{/unless}}>
            <span style="display: none;">
                {{> components/products/price-label
                    label_value=theme_settings.pdp-retail-price-label
                }}
            </span>
            <span data-product-rrp-with-tax class="price price--rrp">
                {{price.rrp_with_tax.formatted}}
            </span>
        </div>
        <div class="price-section price-section--withTax">
            <span class="price-label"style="display: none;">
                {{theme_settings.pdp-price-label}}
            </span>
            <span class="price-now-label" style="display: none;">
                {{> components/products/price-label
                    label_value=theme_settings.pdp-sale-price-label
                }}
            </span>
            <span data-product-price-with-tax class="price price--withTax" data-value-price="{{price.with_tax.formatted}}">{{price.with_tax.formatted}}</span>
            {{#if price.without_tax}}
                <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
        <div class="price-section price-section--withTax non-sale-price--withTax{{#unless price.non_sale_price_with_tax}} price-none{{/unless}}" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
            <span style="display: none;">
                {{> components/products/price-label
                    label_value=theme_settings.pdp-non-sale-price-label
                }}
            </span>
            <span data-product-non-sale-price-with-tax class="price price--non-sale">
                {{price.non_sale_price_with_tax.formatted}}
            </span>
        </div>
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}{{#unless price.non_sale_price_without_tax}} price-none{{/unless}}" {{#unless price.rrp_without_tax}}style="display: none;"{{/unless}}>
            <span style="display: none;">
                {{> components/products/price-label
                    label_value=theme_settings.pdp-retail-price-label
                }}
            </span>
            <span data-product-rrp-price-without-tax class="price price--rrp">
                {{price.rrp_without_tax.formatted}}
            </span>
        </div>
        <div class="price-section price-section--withoutTax">
            <span class="price-label" style="display: none;">
                {{theme_settings.pdp-price-label}}
            </span>
            <span class="price-now-label" style="display: none;">
                {{> components/products/price-label
                    label_value=theme_settings.pdp-sale-price-label
                }}
            </span>
            <span data-product-price-without-tax class="price price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" data-value-price="{{price.without_tax.formatted}}">{{price.without_tax.formatted}}</span>
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
        <div class="price-section price-section--withoutTax non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}{{#unless price.non_sale_price_without_tax}} price-none{{/unless}}" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
            <span style="display: none;">
                {{> components/products/price-label
                    label_value=theme_settings.pdp-non-sale-price-label
                }}
            </span>
            <span data-product-non-sale-price-without-tax class="price price--non-sale">
                {{price.non_sale_price_without_tax.formatted}}
            </span>
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
         <div class="price-section price-section--saving price" {{#unless price.saved}}style="display: none;"{{/unless}}>
                <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                <span data-product-price-saved class="price price--saving">
                    {{price.saved.formatted}}
                </span>
                <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
         </div>
    {{/if}}
{{/and}}

components/products/price-range:
{{#and price.retail_price_range.min.with_tax price.retail_price_range.max.with_tax}}
    <div class="price-section price-section--withTax rrp-price--withTax price-none">
        <span style="display: none;">
            {{> components/products/price-label
                label_value=theme_settings.pdp-retail-price-label
            }}
        </span>
        {{#if price-range-custom}}
            <span data-product-rrp-price-with-tax class="price price--rrp"><span class="price-from color-secondary d-block d-inline-block-xxl uppercase f-size-tiny f-medium">{{lang 'common.from'}}</span>{{price.retail_price_range.min.with_tax.formatted}}</span>
        {{else}}
            <span data-product-rrp-price-with-tax class="price price--rrp">{{price.retail_price_range.min.with_tax.formatted}} - {{price.retail_price_range.max.with_tax.formatted}}</span>
        {{/if}}
    </div>
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax rrp-price--withTax price-none" style="display: none;">
            <span style="display: none;">
                {{> components/products/price-label
                    label_value=theme_settings.pdp-retail-price-label
                }}
            </span>
            <span data-product-rrp-with-tax class="price price--rrp">
                {{price.rrp_with_tax.formatted}}
            </span>
        </div>
    {{/if}}
{{/and}}
{{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
    {{!-- Never display the "non-sales price" if there is a price range to be shown, but we do want the element on the page --}}
    <div class="price-section price-section--withTax">
        <span class="price-label" style="display: none;">{{theme_settings.pdp-price-label}}</span>
        <span class="price-now-label" style="display: none;">
            {{> components/products/price-label
                label_value=theme_settings.pdp-sale-price-label
            }}
        </span>
        {{#if price-range-custom}}
            <span data-product-price-with-tax class="price price--withTax"><span class="price-from color-secondary d-block d-inline-block-xxl uppercase f-size-tiny f-medium">{{lang 'common.from'}}</span>{{price.price_range.min.with_tax.formatted}}</span>
        {{else}}
            <span data-product-price-with-tax class="price price--withTax">{{price.price_range.min.with_tax.formatted}} - {{price.price_range.max.with_tax.formatted}}</span>
        {{/if}}
        {{#and price.price_range.min.without_tax price.price_range.max.without_tax}}
            <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.price_range.min.tax_label}}</abbr>
        {{/and}}
    </div>
    <div class="price-section price-section--withTax non-sale-price--withTax price-none" style="display: none;">
        <span style="display: none;">
            {{> components/products/price-label
                label_value=theme_settings.pdp-non-sale-price-label
            }}
        </span>
        <span data-product-non-sale-price-with-tax class="price price--non-sale">
            {{price.non_sale_price_with_tax.formatted}}
        </span>
    </div>
{{/and}}
{{#and price.retail_price_range.min.without_tax price.retail_price_range.max.without_tax}}
    <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}} price-none">
        <span style="display: none;">
            {{> components/products/price-label
                label_value=theme_settings.pdp-retail-price-label
            }}
        </span>
        {{#if price-range-custom}}
            <span data-product-rrp-price-without-tax class="price price--rrp"><span class="price-from color-secondary d-block d-inline-block-xxl uppercase f-size-tiny f-medium">{{lang 'common.from'}}</span>{{price.retail_price_range.min.without_tax.formatted}}</span>
        {{else}}
            <span data-product-rrp-price-without-tax class="price price--rrp">{{price.retail_price_range.min.without_tax.formatted}} - {{price.retail_price_range.max.without_tax.formatted}}</span>
        {{/if}}
    </div>
{{else}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}} price-none" style="display: none;">
            <span style="display: none;">
                {{> components/products/price-label
                    label_value=theme_settings.pdp-retail-price-label
                }}
            </span>
            <span data-product-rrp-price-without-tax class="price price--rrp">
                {{price.rrp_without_tax.formatted}}
            </span>
        </div>
    {{/if}}
{{/and}}
{{#and price.price_range.min.without_tax price.price_range.max.without_tax}}
    {{!-- Never display the "non-sales price" if there is a price range to be shown, but we do want the element on the page --}}
    <div class="price-section price-section--withoutTax{{#and price_range.min.with_tax price_range.max.with_tax}} price-section--minor{{/and}}">
        <span class="price-label" style="display: none;">{{theme_settings.pdp-price-label}}</span>
        <span class="price-now-label" style="display: none;">
            {{> components/products/price-label
                label_value=theme_settings.pdp-sale-price-label
            }}
        </span>
        {{#if price-range-custom}}
            <span data-product-price-without-tax class="price price--withoutTax"><span class="price-from color-secondary d-block d-inline-block-xxl uppercase f-size-tiny f-medium">{{lang 'common.from'}}</span>{{price.price_range.min.without_tax.formatted}}</span>
        {{else}}
            <span data-product-price-without-tax class="price price--withoutTax">{{price.price_range.min.without_tax.formatted}} - {{price.price_range.max.without_tax.formatted}}</span>
        {{/if}}
        {{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
            <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.price_range.min.tax_label}}</abbr>
        {{/and}}
    </div>
    <div class="price-section price-section--withoutTax non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}} price-none" style="display: none;">
        <span style="display: none;">
            {{> components/products/price-label
                label_value=theme_settings.pdp-non-sale-price-label
            }}
        </span>
        <span data-product-non-sale-price-without-tax class="price price--non-sale">
            {{price.non_sale_price_without_tax.formatted}}
        </span>
    </div>
{{/and}}

{{!-- Never display the "saving price" element by default if there is a price range on the page to be shown --}}
{{#if page_type '===' 'product'}}
     <div class="price-section price-section--saving price" style="display: none;">
            <span class="price">{{lang 'products.you_save_opening_text'}}</span>
            <span data-product-price-saved class="price price--saving">
                {{price.saved.formatted}}
            </span>
            <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
     </div>
{{/if}}

components/products/product-aria-label:
{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
    {{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
{{lang 'category.shop_by_price_range_aria'
       from=price.price_range.min.with_tax.formatted
       to=price.price_range.max.with_tax.formatted
}}
    {{else}}
{{lang 'category.shop_by_price_range_aria'
       from=price.price_range.min.without_tax.formatted
       to=price.price_range.max.without_tax.formatted
}}
    {{/and}}
{{else}}
    {{#if price.with_tax}}
        {{#if price.non_sale_price_with_tax}}
{{> components/products/price-label
    label_value=theme_settings.pdp-non-sale-price-label
    fallback=(lang "page_builder.pdp-non-sale-price-label")
}}{{price.non_sale_price_with_tax.formatted}},
{{> components/products/price-label
    label_value=theme_settings.pdp-sale-price-label
    fallback=(lang "page_builder.pdp-sale-price-label")
}}{{price.with_tax.formatted}}
        {{else}}
{{price.with_tax.formatted}}
        {{/if}}
    {{else}}
        {{#if price.non_sale_price_without_tax}}
{{> components/products/price-label
    label_value=theme_settings.pdp-non-sale-price-label
    fallback=(lang "page_builder.pdp-non-sale-price-label")
}}{{price.non_sale_price_without_tax.formatted}},
{{> components/products/price-label
    label_value=theme_settings.pdp-sale-price-label
    fallback=(lang "page_builder.pdp-sale-price-label")
}}{{price.without_tax.formatted}}
        {{else}}
{{price.without_tax.formatted}}
        {{/if}}
    {{/if}}
{{/and}}

components/products/product-badge:
{{#if theme_settings.halo_label_product}}
    <div class="halo-badges left">
        {{#each productsnew}}
            {{#if id '==' ../id}}
                <div class="new-badge halo-badge center">
                    <span class="text f-root f-size-small-tb f-size-tiny-mb">{{lang 'products.new_label'}}</span>
                </div>
            {{/if}}
        {{/each}}
        {{#or price.non_sale_price_with_tax price.non_sale_price_without_tax}}
            <div class="sale-badge halo-badge center">
                {{#if theme_settings.sale_badges_type '==' 'percent'}}
                    {{#if price.non_sale_price_with_tax}}
                        <span class="text f-root f-size-small-tb f-size-tiny-mb">{{lang 'products.sale_label'}}{{round (subtract 100 (multiply (divide price.with_tax.value price.non_sale_price_with_tax.value) 100)) 0}}%</span>
                    {{/if}}
                    {{#if price.non_sale_price_without_tax}}
                        <span class="text f-root f-size-small-tb f-size-tiny-mb">{{lang 'products.sale_label'}}{{round (subtract 100 (multiply (divide price.without_tax.value price.non_sale_price_without_tax.value) 100)) 0}}%</span>
                    {{/if}}
                {{else}}
                    <span class="text f-root f-size-small-tb f-size-tiny-mb">{{lang 'products.badge_sale'}}</span>
                {{/if}}
            </div>
        {{/or}}
        {{#each custom_fields}}
            {{#if name '===' '__customlabel'}}
                <div class="custom-badge halo-badge center">
                    <span class="text f-root f-size-small-tb f-size-tiny-mb">{{{value}}}</span>
                </div>
            {{/if}}
        {{/each}}
        {{#if out_of_stock_message}}
            <div class="soldOut-badge halo-badge center">
                <span class="text f-root f-size-small-tb f-size-tiny-mb">{{lang 'products.sold_out'}}</span>
            </div>
        {{/if}}
    </div>
{{/if}}
